package vn.ctu.edu.chatservice.config;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.data.redis.connection.RedisConnectionFactory;
import org.springframework.data.redis.core.RedisTemplate;
import org.springframework.data.redis.serializer.GenericJackson2JsonRedisSerializer;
import org.springframework.data.redis.serializer.StringRedisSerializer;
import org.springframework.web.client.RestTemplate;

@Configuration
public class AppConfig {

    @Bean
    public RestTemplate restTemplate() {
        return new RestTemplate();
    }

    @Bean
    public RedisTemplate<String, Object> redisTemplate(RedisConnectionFactory connectionFactory) {
        RedisTemplate<String, Object> template = new RedisTemplate<>();
        template.setConnectionFactory(connectionFactory);

        // Use String serializer for keys
        template.setKeySerializer(new StringRedisSerializer());
        template.setHashKeySerializer(new StringRedisSerializer());

        // Use JSON serializer for values
        template.setValueSerializer(new GenericJackson2JsonRedisSerializer());
        template.setHashValueSerializer(new GenericJackson2JsonRedisSerializer());

        template.afterPropertiesSet();
        return template;
    }
}

package vn.ctu.edu.chatservice.config;

import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.security.config.annotation.web.builders.HttpSecurity;
import org.springframework.security.config.annotation.web.configuration.EnableWebSecurity;
import org.springframework.security.config.http.SessionCreationPolicy;
import org.springframework.security.web.SecurityFilterChain;
import org.springframework.security.web.authentication.UsernamePasswordAuthenticationFilter;
import org.springframework.web.cors.CorsConfiguration;
import org.springframework.web.cors.CorsConfigurationSource;
import org.springframework.web.cors.UrlBasedCorsConfigurationSource;

import java.util.Arrays;

@Configuration
@EnableWebSecurity
public class SecurityConfig {

    @Bean
    public SecurityFilterChain filterChain(HttpSecurity http) throws Exception {
        http.cors().and().csrf().disable()
            .sessionManagement().sessionCreationPolicy(SessionCreationPolicy.STATELESS)
            .and()
            .authorizeHttpRequests(authz -> authz
                .requestMatchers("/ws/chat/**").permitAll()
                .requestMatchers("/actuator/**").permitAll()
                .anyRequest().authenticated()
            );
            // TODO: Add JWT filter
            // .addFilterBefore(jwtAuthenticationFilter, UsernamePasswordAuthenticationFilter.class);

        return http.build();
    }

    @Bean
    public CorsConfigurationSource corsConfigurationSource() {
        CorsConfiguration configuration = new CorsConfiguration();
        configuration.setAllowedOriginPatterns(Arrays.asList("http://localhost:3000", "http://localhost:3001"));
        configuration.setAllowedMethods(Arrays.asList("GET", "POST", "PUT", "DELETE", "OPTIONS"));
        configuration.setAllowedHeaders(Arrays.asList("*"));
        configuration.setAllowCredentials(true);

        UrlBasedCorsConfigurationSource source = new UrlBasedCorsConfigurationSource();
        source.registerCorsConfiguration("/**", configuration);
        return source;
    }
}

package vn.ctu.edu.chatservice.config;

import lombok.RequiredArgsConstructor;
import org.springframework.context.annotation.Configuration;
import org.springframework.messaging.Message;
import org.springframework.messaging.MessageChannel;
import org.springframework.messaging.simp.config.ChannelRegistration;
import org.springframework.messaging.simp.config.MessageBrokerRegistry;
import org.springframework.messaging.simp.stomp.StompCommand;
import org.springframework.messaging.simp.stomp.StompHeaderAccessor;
import org.springframework.messaging.support.ChannelInterceptor;
import org.springframework.messaging.support.MessageHeaderAccessor;
import org.springframework.web.socket.config.annotation.EnableWebSocketMessageBroker;
import org.springframework.web.socket.config.annotation.StompEndpointRegistry;
import org.springframework.web.socket.config.annotation.WebSocketMessageBrokerConfigurer;
import vn.ctu.edu.chatservice.service.UserPresenceService;

@Configuration
@EnableWebSocketMessageBroker
@RequiredArgsConstructor
public class WebSocketConfig implements WebSocketMessageBrokerConfigurer {

    private final UserPresenceService userPresenceService;

    @Override
    public void configureMessageBroker(MessageBrokerRegistry config) {
        // Enable simple broker for sending messages to clients
        config.enableSimpleBroker("/topic", "/queue");

        // Prefix for messages that are bound for methods annotated with @MessageMapping
        config.setApplicationDestinationPrefixes("/app");

        // Prefix for user-specific destinations
        config.setUserDestinationPrefix("/user");
    }

    @Override
    public void registerStompEndpoints(StompEndpointRegistry registry) {
        // Register STOMP endpoint
        registry.addEndpoint("/ws/chat")
                .setAllowedOriginPatterns("http://localhost:3000", "http://localhost:3001")
                .withSockJS();
    }

    @Override
    public void configureClientInboundChannel(ChannelRegistration registration) {
        registration.interceptors(new ChannelInterceptor() {
            @Override
            public Message<?> preSend(Message<?> message, MessageChannel channel) {
                StompHeaderAccessor accessor = MessageHeaderAccessor.getAccessor(message, StompHeaderAccessor.class);

                if (StompCommand.CONNECT.equals(accessor.getCommand())) {
                    // Extract user ID from headers
                    String userId = accessor.getFirstNativeHeader("userId");
                    String sessionId = accessor.getSessionId();

                    if (userId != null && sessionId != null) {
                        accessor.setUser(() -> userId);
                        userPresenceService.setUserOnline(userId, sessionId);
                    }
                } else if (StompCommand.DISCONNECT.equals(accessor.getCommand())) {
                    String userId = accessor.getUser() != null ? accessor.getUser().getName() : null;
                    if (userId != null) {
                        userPresenceService.setUserOffline(userId);
                    }
                }

                return message;
            }
        });
    }
}

package vn.ctu.edu.chatservice.controller;

import lombok.RequiredArgsConstructor;
import org.springframework.data.domain.Page;
import org.springframework.http.ResponseEntity;
import org.springframework.messaging.handler.annotation.MessageMapping;
import org.springframework.messaging.handler.annotation.Payload;
import org.springframework.security.core.Authentication;
import org.springframework.web.bind.annotation.*;
import vn.ctu.edu.chatservice.dto.request.*;
import vn.ctu.edu.chatservice.dto.response.*;
import vn.ctu.edu.chatservice.service.ConversationService;
import vn.ctu.edu.chatservice.service.UserPresenceService;

import jakarta.validation.Valid;
import java.util.List;

@RestController
@RequestMapping("/api/conversations")
@RequiredArgsConstructor
public class ConversationController {

    private final ConversationService conversationService;
    private final UserPresenceService userPresenceService;

    @PostMapping
    public ResponseEntity<ConversationResponse> createConversation(
            @Valid @RequestBody CreateConversationRequest request,
            Authentication authentication) {

        String userId = authentication.getName();
        ConversationResponse response = conversationService.createConversation(request, userId);
        return ResponseEntity.ok(response);
    }

    @GetMapping
    public ResponseEntity<Page<ConversationResponse>> getUserConversations(
            @RequestParam(defaultValue = "0") int page,
            @RequestParam(defaultValue = "20") int size,
            Authentication authentication) {

        String userId = authentication.getName();
        Page<ConversationResponse> conversations = conversationService.getUserConversations(userId, page, size);
        return ResponseEntity.ok(conversations);
    }

    @GetMapping("/{conversationId}")
    public ResponseEntity<ConversationResponse> getConversation(
            @PathVariable String conversationId,
            Authentication authentication) {

        String userId = authentication.getName();
        ConversationResponse response = conversationService.getConversationById(conversationId, userId);
        return ResponseEntity.ok(response);
    }

    @PutMapping("/{conversationId}")
    public ResponseEntity<ConversationResponse> updateConversation(
            @PathVariable String conversationId,
            @Valid @RequestBody UpdateConversationRequest request,
            Authentication authentication) {

        String userId = authentication.getName();
        ConversationResponse response = conversationService.updateConversation(conversationId, request, userId);
        return ResponseEntity.ok(response);
    }

    @PostMapping("/{conversationId}/participants")
    public ResponseEntity<Void> addParticipant(
            @PathVariable String conversationId,
            @RequestParam String participantId,
            Authentication authentication) {

        String userId = authentication.getName();
        conversationService.addParticipant(conversationId, participantId, userId);
        return ResponseEntity.ok().build();
    }

    @DeleteMapping("/{conversationId}/participants/{participantId}")
    public ResponseEntity<Void> removeParticipant(
            @PathVariable String conversationId,
            @PathVariable String participantId,
            Authentication authentication) {

        String userId = authentication.getName();
        conversationService.removeParticipant(conversationId, participantId, userId);
        return ResponseEntity.ok().build();
    }

    @GetMapping("/search")
    public ResponseEntity<List<ConversationResponse>> searchConversations(
            @RequestParam String query,
            Authentication authentication) {

        String userId = authentication.getName();
        List<ConversationResponse> results = conversationService.searchConversations(userId, query);
        return ResponseEntity.ok(results);
    }

    @DeleteMapping("/{conversationId}")
    public ResponseEntity<Void> deleteConversation(
            @PathVariable String conversationId,
            Authentication authentication) {

        String userId = authentication.getName();
        conversationService.deleteConversation(conversationId, userId);
        return ResponseEntity.noContent().build();
    }

    // WebSocket message mappings for real-time features
    @MessageMapping("/conversation/{conversationId}/typing")
    public void handleTyping(@Payload TypingRequest request, Authentication authentication) {
        String userId = authentication.getName();
        userPresenceService.setTypingStatus(userId, request.getConversationId(), request.isTyping());
    }
}

package vn.ctu.edu.chatservice.controller;

import lombok.RequiredArgsConstructor;
import org.springframework.http.ResponseEntity;
import org.springframework.messaging.handler.annotation.MessageMapping;
import org.springframework.messaging.handler.annotation.Payload;
import org.springframework.security.core.Authentication;
import org.springframework.web.bind.annotation.*;
import vn.ctu.edu.chatservice.dto.request.AddReactionRequest;
import vn.ctu.edu.chatservice.dto.request.SendMessageRequest;
import vn.ctu.edu.chatservice.dto.response.ChatPageResponse;
import vn.ctu.edu.chatservice.dto.response.MessageResponse;
import vn.ctu.edu.chatservice.service.MessageService;

import jakarta.validation.Valid;
import java.util.List;

@RestController
@RequestMapping("/api/messages")
@RequiredArgsConstructor
public class MessageController {

    private final MessageService messageService;

    @PostMapping
    public ResponseEntity<MessageResponse> sendMessage(
            @Valid @RequestBody SendMessageRequest request,
            Authentication authentication) {

        String userId = authentication.getName();
        MessageResponse response = messageService.sendMessage(request, userId);
        return ResponseEntity.ok(response);
    }

    @GetMapping("/conversation/{conversationId}")
    public ResponseEntity<ChatPageResponse<MessageResponse>> getMessages(
            @PathVariable String conversationId,
            @RequestParam(defaultValue = "0") int page,
            @RequestParam(defaultValue = "50") int size,
            Authentication authentication) {

        String userId = authentication.getName();
        ChatPageResponse<MessageResponse> messages = messageService.getMessages(conversationId, userId, page, size);
        return ResponseEntity.ok(messages);
    }

    @PutMapping("/{messageId}")
    public ResponseEntity<MessageResponse> editMessage(
            @PathVariable String messageId,
            @RequestParam String content,
            Authentication authentication) {

        String userId = authentication.getName();
        MessageResponse response = messageService.editMessage(messageId, content, userId);
        return ResponseEntity.ok(response);
    }

    @DeleteMapping("/{messageId}")
    public ResponseEntity<Void> deleteMessage(
            @PathVariable String messageId,
            Authentication authentication) {

        String userId = authentication.getName();
        messageService.deleteMessage(messageId, userId);
        return ResponseEntity.noContent().build();
    }

    @PostMapping("/reactions")
    public ResponseEntity<MessageResponse> addReaction(
            @Valid @RequestBody AddReactionRequest request,
            Authentication authentication) {

        String userId = authentication.getName();
        MessageResponse response = messageService.addReaction(request, userId);
        return ResponseEntity.ok(response);
    }

    @DeleteMapping("/{messageId}/reactions")
    public ResponseEntity<Void> removeReaction(
            @PathVariable String messageId,
            Authentication authentication) {

        String userId = authentication.getName();
        messageService.removeReaction(messageId, userId);
        return ResponseEntity.noContent().build();
    }

    @GetMapping("/conversation/{conversationId}/search")
    public ResponseEntity<List<MessageResponse>> searchMessages(
            @PathVariable String conversationId,
            @RequestParam String query,
            Authentication authentication) {

        String userId = authentication.getName();
        List<MessageResponse> results = messageService.searchMessages(conversationId, query, userId);
        return ResponseEntity.ok(results);
    }

    @GetMapping("/conversation/{conversationId}/unread-count")
    public ResponseEntity<Long> getUnreadCount(
            @PathVariable String conversationId,
            Authentication authentication) {

        String userId = authentication.getName();
        long count = messageService.getUnreadCount(conversationId, userId);
        return ResponseEntity.ok(count);
    }

    @PostMapping("/conversation/{conversationId}/mark-read")
    public ResponseEntity<Void> markAsRead(
            @PathVariable String conversationId,
            Authentication authentication) {

        String userId = authentication.getName();
        messageService.markMessagesAsRead(conversationId, userId);
        return ResponseEntity.ok().build();
    }

    // WebSocket message mappings for real-time messaging
    @MessageMapping("/message.send")
    public void sendMessageViaWebSocket(@Payload SendMessageRequest request, Authentication authentication) {
        String userId = authentication.getName();
        messageService.sendMessage(request, userId);
    }

    @MessageMapping("/message.reaction")
    public void addReactionViaWebSocket(@Payload AddReactionRequest request, Authentication authentication) {
        String userId = authentication.getName();
        messageService.addReaction(request, userId);
    }
}

package vn.ctu.edu.chatservice.controller;

import lombok.RequiredArgsConstructor;
import org.springframework.http.ResponseEntity;
import org.springframework.security.core.Authentication;
import org.springframework.web.bind.annotation.*;
import vn.ctu.edu.chatservice.dto.response.UserPresenceResponse;
import vn.ctu.edu.chatservice.service.UserPresenceService;

import java.util.List;

@RestController
@RequestMapping("/api/presence")
@RequiredArgsConstructor
public class UserPresenceController {

    private final UserPresenceService userPresenceService;

    @GetMapping("/{userId}")
    public ResponseEntity<UserPresenceResponse> getUserPresence(@PathVariable String userId) {
        UserPresenceResponse presence = userPresenceService.getUserPresence(userId);
        return ResponseEntity.ok(presence);
    }

    @GetMapping("/users")
    public ResponseEntity<List<UserPresenceResponse>> getMultipleUserPresence(
            @RequestParam List<String> userIds) {
        List<UserPresenceResponse> presences = userPresenceService.getMultipleUserPresence(userIds);
        return ResponseEntity.ok(presences);
    }

    @GetMapping("/online")
    public ResponseEntity<List<UserPresenceResponse>> getOnlineUsers() {
        List<UserPresenceResponse> onlineUsers = userPresenceService.getOnlineUsers();
        return ResponseEntity.ok(onlineUsers);
    }

    @PostMapping("/away")
    public ResponseEntity<Void> setAway(Authentication authentication) {
        String userId = authentication.getName();
        userPresenceService.setUserAway(userId);
        return ResponseEntity.ok().build();
    }

    @GetMapping("/conversation/{conversationId}/typing")
    public ResponseEntity<List<String>> getTypingUsers(@PathVariable String conversationId) {
        List<String> typingUsers = userPresenceService.getTypingUsers(conversationId);
        return ResponseEntity.ok(typingUsers);
    }
}

package vn.ctu.edu.chatservice.dto.request;

import lombok.AllArgsConstructor;
import lombok.Data;
import lombok.NoArgsConstructor;
import vn.ctu.edu.chatservice.model.Conversation;

import jakarta.validation.constraints.NotBlank;
import jakarta.validation.constraints.NotEmpty;
import jakarta.validation.constraints.Size;
import java.util.List;

@Data
@NoArgsConstructor
@AllArgsConstructor
public class CreateConversationRequest {
    @Size(max = 100, message = "Tên nhóm không được vượt quá 100 ký tự")
    private String name;

    @NotEmpty(message = "Danh sách thành viên không được trống")
    private List<String> participantIds;

    private Conversation.ConversationType type = Conversation.ConversationType.DIRECT;

    private String description;

    private String avatarUrl;
}

@Data
@NoArgsConstructor
@AllArgsConstructor
class SendMessageRequest {
    @NotBlank(message = "ID conversation không được trống")
    private String conversationId;

    @NotBlank(message = "Nội dung tin nhắn không được trống")
    @Size(max = 2000, message = "Tin nhắn không được vượt quá 2000 ký tự")
    private String content;

    private String replyToMessageId;
