package vn.ctu.edu.chatservice.service;

import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.PageRequest;
import org.springframework.data.domain.Pageable;
import org.springframework.data.domain.Sort;
import org.springframework.stereotype.Service;
import vn.ctu.edu.chatservice.dto.request.CreateConversationRequest;
import vn.ctu.edu.chatservice.dto.request.UpdateConversationRequest;
import vn.ctu.edu.chatservice.dto.response.ConversationResponse;
import vn.ctu.edu.chatservice.dto.response.ParticipantInfo;
import vn.ctu.edu.chatservice.exception.ChatException;
import vn.ctu.edu.chatservice.model.Conversation;
import vn.ctu.edu.chatservice.repository.ConversationRepository;
import vn.ctu.edu.chatservice.repository.MessageRepository;

import java.time.LocalDateTime;
import java.util.List;
import java.util.Optional;
import java.util.stream.Collectors;

@Service
@RequiredArgsConstructor
@Slf4j
public class ConversationService {

    private final ConversationRepository conversationRepository;
    private final MessageRepository messageRepository;
    private final UserPresenceService userPresenceService;
    private final UserService userService;

    public ConversationResponse createConversation(CreateConversationRequest request, String createdBy) {
        log.info("Creating conversation with type: {} by user: {}", request.getType(), createdBy);

        // Validate participants
        if (request.getParticipantIds().isEmpty()) {
            throw new ChatException("Danh sách thành viên không được trống");
        }

        // Kiểm tra nếu là chat trực tiếp giữa 2 người đã tồn tại
        if (request.getType() == Conversation.ConversationType.DIRECT && 
            request.getParticipantIds().size() == 1) {

            String otherUserId = request.getParticipantIds().get(0);
            Optional<Conversation> existingConversation = 
                conversationRepository.findDirectConversationBetweenUsers(createdBy, otherUserId);

            if (existingConversation.isPresent()) {
                return convertToResponse(existingConversation.get());
            }
        }

        // Tạo conversation mới
        Conversation conversation = new Conversation();
        conversation.setName(request.getName());
        conversation.setType(request.getType());

        // Thêm creator vào danh sách participants
        List<String> participants = request.getParticipantIds();
        if (!participants.contains(createdBy)) {
            participants.add(createdBy);
        }
        conversation.setParticipantIds(participants);

        conversation.setCreatedBy(createdBy);
        conversation.setCreatedAt(LocalDateTime.now());
        conversation.setUpdatedAt(LocalDateTime.now());

        // Metadata cho group chat
        if (request.getType() == Conversation.ConversationType.GROUP) {
            ConversationMetadata metadata = new ConversationMetadata();
            metadata.setDescription(request.getDescription());
            metadata.setAvatarUrl(request.getAvatarUrl());
            metadata.getAdminIds().add(createdBy); // Creator là admin
            conversation.setMetadata(metadata);
        }

        Conversation saved = conversationRepository.save(conversation);
        log.info("Created conversation: {} with {} participants", saved.getId(), participants.size());

        return convertToResponse(saved);
    }

    public Page<ConversationResponse> getUserConversations(String userId, int page, int size) {
        Pageable pageable = PageRequest.of(page, size, Sort.by("lastMessageAt").descending());
        Page<Conversation> conversations = conversationRepository.findByParticipantIdsContaining(userId, pageable);

        return conversations.map(this::convertToResponse);
    }

    public ConversationResponse getConversationById(String conversationId, String userId) {
        Conversation conversation = conversationRepository.findById(conversationId)
            .orElseThrow(() -> new ChatException("Không tìm thấy cuộc trò chuyện"));

        // Kiểm tra quyền truy cập
        if (!conversation.getParticipantIds().contains(userId)) {
            throw new ChatException("Bạn không có quyền truy cập cuộc trò chuyện này");
        }

        return convertToResponse(conversation);
    }

    public ConversationResponse updateConversation(String conversationId, UpdateConversationRequest request, String userId) {
        Conversation conversation = conversationRepository.findById(conversationId)
            .orElseThrow(() -> new ChatException("Không tìm thấy cuộc trò chuyện"));

        // Kiểm tra quyền sửa đổi
        if (!canModifyConversation(conversation, userId)) {
            throw new ChatException("Bạn không có quyền sửa đổi cuộc trò chuyện này");
        }

        // Cập nhật thông tin
        if (request.getName() != null) {
            conversation.setName(request.getName());
        }

        if (conversation.getMetadata() != null) {
            if (request.getDescription() != null) {
                conversation.getMetadata().setDescription(request.getDescription());
            }
            if (request.getAvatarUrl() != null) {
                conversation.getMetadata().setAvatarUrl(request.getAvatarUrl());
            }
        }

        if (request.getParticipantIds() != null) {
            conversation.setParticipantIds(request.getParticipantIds());
        }

        conversation.setUpdatedAt(LocalDateTime.now());

        Conversation updated = conversationRepository.save(conversation);
        return convertToResponse(updated);
    }

    public void addParticipant(String conversationId, String participantId, String addedBy) {
        Conversation conversation = conversationRepository.findById(conversationId)
            .orElseThrow(() -> new ChatException("Không tìm thấy cuộc trò chuyện"));

        if (!canAddMembers(conversation, addedBy)) {
            throw new ChatException("Bạn không có quyền thêm thành viên");
        }

        if (!conversation.getParticipantIds().contains(participantId)) {
            conversation.getParticipantIds().add(participantId);
            conversation.setUpdatedAt(LocalDateTime.now());
            conversationRepository.save(conversation);

            log.info("Added participant {} to conversation {} by {}", participantId, conversationId, addedBy);
        }
    }

    public void removeParticipant(String conversationId, String participantId, String removedBy) {
        Conversation conversation = conversationRepository.findById(conversationId)
            .orElseThrow(() -> new ChatException("Không tìm thấy cuộc trò chuyện"));

        // Chỉ admin hoặc chính user đó mới có thể remove
        if (!canRemoveMembers(conversation, removedBy) && !participantId.equals(removedBy)) {
            throw new ChatException("Bạn không có quyền xóa thành viên");
        }

        conversation.getParticipantIds().remove(participantId);
        conversation.setUpdatedAt(LocalDateTime.now());
        conversationRepository.save(conversation);

        log.info("Removed participant {} from conversation {} by {}", participantId, conversationId, removedBy);
    }

    public List<ConversationResponse> searchConversations(String userId, String query) {
        List<Conversation> conversations = conversationRepository
            .findByParticipantIdsContainingAndNameContainingIgnoreCase(userId, query);

        return conversations.stream()
            .map(this::convertToResponse)
            .collect(Collectors.toList());
    }

    public void deleteConversation(String conversationId, String userId) {
        Conversation conversation = conversationRepository.findById(conversationId)
            .orElseThrow(() -> new ChatException("Không tìm thấy cuộc trò chuyện"));

        // Chỉ creator hoặc admin mới có thể xóa
        if (!canDeleteConversation(conversation, userId)) {
            throw new ChatException("Bạn không có quyền xóa cuộc trò chuyện này");
        }

        // Xóa tất cả messages trong conversation
        messageRepository.deleteByConversationId(conversationId);

        // Xóa conversation
        conversationRepository.deleteById(conversationId);

        log.info("Deleted conversation {} by user {}", conversationId, userId);
    }

    private ConversationResponse convertToResponse(Conversation conversation) {
        ConversationResponse response = new ConversationResponse();
        response.setId(conversation.getId());
        response.setName(conversation.getName());
        response.setType(conversation.getType());
        response.setCreatedAt(conversation.getCreatedAt());
        response.setUpdatedAt(conversation.getUpdatedAt());
        response.setLastMessageAt(conversation.getLastMessageAt());

        // Lấy thông tin participants
        List<ParticipantInfo> participants = conversation.getParticipantIds().stream()
            .map(this::getParticipantInfo)
            .collect(Collectors.toList());
        response.setParticipants(participants);

        // Avatar cho group chat
        if (conversation.getMetadata() != null) {
            response.setAvatarUrl(conversation.getMetadata().getAvatarUrl());
        }

        // Đếm unread messages (sẽ implement sau)
        response.setUnreadCount(0);

        return response;
    }

    private ParticipantInfo getParticipantInfo(String userId) {
        // Tích hợp với UserService để lấy thông tin user
        // và UserPresenceService để lấy trạng thái online
        ParticipantInfo info = new ParticipantInfo();
        info.setUserId(userId);
        // TODO: Implement user info retrieval
        return info;
    }

    private boolean canModifyConversation(Conversation conversation, String userId) {
        if (conversation.getType() == Conversation.ConversationType.DIRECT) {
            return conversation.getParticipantIds().contains(userId);
        }

        // Group chat: chỉ admin mới được sửa
        return conversation.getMetadata() != null && 
               conversation.getMetadata().getAdminIds().contains(userId);
    }

    private boolean canAddMembers(Conversation conversation, String userId) {
        if (conversation.getSettings() != null && 
            !conversation.getSettings().isAllowMembersToAddOthers()) {
            return conversation.getMetadata() != null && 
                   conversation.getMetadata().getAdminIds().contains(userId);
        }
        return conversation.getParticipantIds().contains(userId);
    }

    private boolean canRemoveMembers(Conversation conversation, String userId) {
        return conversation.getMetadata() != null && 
               conversation.getMetadata().getAdminIds().contains(userId);
    }

    private boolean canDeleteConversation(Conversation conversation, String userId) {
        return conversation.getCreatedBy().equals(userId) ||
               (conversation.getMetadata() != null && 
                conversation.getMetadata().getAdminIds().contains(userId));
    }
}

package vn.ctu.edu.chatservice.service;

import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.PageRequest;
import org.springframework.data.domain.Pageable;
import org.springframework.data.domain.Sort;
import org.springframework.stereotype.Service;
import vn.ctu.edu.chatservice.dto.request.AddReactionRequest;
import vn.ctu.edu.chatservice.dto.request.SendMessageRequest;
import vn.ctu.edu.chatservice.dto.response.MessageResponse;
import vn.ctu.edu.chatservice.dto.response.ChatPageResponse;
import vn.ctu.edu.chatservice.exception.ChatException;
import vn.ctu.edu.chatservice.model.*;
import vn.ctu.edu.chatservice.repository.ConversationRepository;
import vn.ctu.edu.chatservice.repository.MessageRepository;

import java.time.LocalDateTime;
import java.util.List;
import java.util.Optional;
import java.util.stream.Collectors;

@Service
@RequiredArgsConstructor
@Slf4j
public class MessageService {

    private final MessageRepository messageRepository;
    private final ConversationRepository conversationRepository;
    private final WebSocketService webSocketService;
    private final NotificationService notificationService;

    public MessageResponse sendMessage(SendMessageRequest request, String senderId) {
        log.info("Sending message from user: {} to conversation: {}", senderId, request.getConversationId());

        // Kiểm tra conversation tồn tại và user có quyền gửi tin nhắn
        Conversation conversation = conversationRepository.findById(request.getConversationId())
            .orElseThrow(() -> new ChatException("Không tìm thấy cuộc trò chuyện"));

        if (!conversation.getParticipantIds().contains(senderId)) {
            throw new ChatException("Bạn không có quyền gửi tin nhắn trong cuộc trò chuyện này");
        }

        // Tạo message mới
        Message message = new Message();
        message.setConversationId(request.getConversationId());
        message.setSenderId(senderId);
        message.setType(Message.MessageType.TEXT);
        message.setContent(request.getContent());
        message.setReplyToMessageId(request.getReplyToMessageId());
        message.setStatus(Message.MessageStatus.SENT);
        message.setCreatedAt(LocalDateTime.now());
        message.setUpdatedAt(LocalDateTime.now());

        // Lấy thông tin sender (cache)
        // TODO: Tích hợp với UserService
        message.setSenderName("User " + senderId);
        message.setSenderAvatar("");

        // Lưu message
        Message savedMessage = messageRepository.save(message);

        // Cập nhật last message của conversation
        conversation.setLastMessageId(savedMessage.getId());
        conversation.setLastMessageAt(savedMessage.getCreatedAt());
        conversation.setUpdatedAt(LocalDateTime.now());
        conversationRepository.save(conversation);

        // Chuyển đổi thành response
        MessageResponse response = convertToResponse(savedMessage);

        // Gửi real-time notification qua WebSocket
        webSocketService.sendMessageToConversation(request.getConversationId(), response);

        // Gửi push notification
        notificationService.sendMessageNotification(conversation, savedMessage);

        log.info("Message sent successfully: {}", savedMessage.getId());
        return response;
    }

    public ChatPageResponse<MessageResponse> getMessages(String conversationId, String userId, int page, int size) {
        // Kiểm tra quyền truy cập
        Conversation conversation = conversationRepository.findById(conversationId)
            .orElseThrow(() -> new ChatException("Không tìm thấy cuộc trò chuyện"));

        if (!conversation.getParticipantIds().contains(userId)) {
            throw new ChatException("Bạn không có quyền truy cập cuộc trò chuyện này");
        }

        // Lấy messages với pagination (sắp xếp theo thời gian giảm dần)
        Pageable pageable = PageRequest.of(page, size, Sort.by("createdAt").descending());
        Page<Message> messages = messageRepository.findByConversationIdAndIsDeletedFalseOrderByCreatedAtDesc(
            conversationId, pageable);

        // Chuyển đổi thành response
        List<MessageResponse> messageResponses = messages.getContent().stream()
            .map(this::convertToResponse)
            .collect(Collectors.toList());

        // Đánh dấu messages là đã đọc
        markMessagesAsRead(conversationId, userId);

        return new ChatPageResponse<>(
            messageResponses,
            messages.getNumber(),
            messages.getSize(),
            messages.getTotalElements(),
            messages.getTotalPages(),
            messages.hasNext(),
            messages.hasPrevious()
        );
    }

    public MessageResponse editMessage(String messageId, String newContent, String userId) {
        Message message = messageRepository.findById(messageId)
            .orElseThrow(() -> new ChatException("Không tìm thấy tin nhắn"));

        // Chỉ người gửi mới có thể sửa tin nhắn
        if (!message.getSenderId().equals(userId)) {
            throw new ChatException("Bạn không có quyền sửa tin nhắn này");
        }

        // Không thể sửa tin nhắn quá cũ (15 phút)
        if (message.getCreatedAt().isBefore(LocalDateTime.now().minusMinutes(15))) {
            throw new ChatException("Không thể sửa tin nhắn sau 15 phút");
        }

        message.setContent(newContent);
        message.setIsEdited(true);
        message.setEditedAt(LocalDateTime.now());
        message.setUpdatedAt(LocalDateTime.now());

        Message updated = messageRepository.save(message);
        MessageResponse response = convertToResponse(updated);

        // Gửi cập nhật real-time
        webSocketService.sendMessageUpdateToConversation(message.getConversationId(), response);

        return response;
    }

    public void deleteMessage(String messageId, String userId) {
        Message message = messageRepository.findById(messageId)
            .orElseThrow(() -> new ChatException("Không tìm thấy tin nhắn"));

        // Chỉ người gửi mới có thể xóa tin nhắn
        if (!message.getSenderId().equals(userId)) {
            throw new ChatException("Bạn không có quyền xóa tin nhắn này");
        }

        message.setIsDeleted(true);
        message.setContent("Tin nhắn đã được xóa");
        message.setUpdatedAt(LocalDateTime.now());

        messageRepository.save(message);

        // Gửi cập nhật real-time
        webSocketService.sendMessageDeleteToConversation(message.getConversationId(), messageId);

        log.info("Message {} deleted by user {}", messageId, userId);
    }

    public MessageResponse addReaction(AddReactionRequest request, String userId) {
        Message message = messageRepository.findById(request.getMessageId())
            .orElseThrow(() -> new ChatException("Không tìm thấy tin nhắn"));

        // Kiểm tra user có quyền react không
        Conversation conversation = conversationRepository.findById(message.getConversationId())
            .orElseThrow(() -> new ChatException("Không tìm thấy cuộc trò chuyện"));

        if (!conversation.getParticipantIds().contains(userId)) {
            throw new ChatException("Bạn không có quyền react tin nhắn này");
        }

        // Kiểm tra xem user đã react chưa
        Optional<MessageReaction> existingReaction = message.getReactions().stream()
            .filter(r -> r.getUserId().equals(userId))
            .findFirst();

        if (existingReaction.isPresent()) {
            // Update existing reaction
            existingReaction.get().setEmoji(request.getEmoji());
            existingReaction.get().setCreatedAt(LocalDateTime.now());
        } else {
            // Add new reaction
            MessageReaction reaction = new MessageReaction();
            reaction.setUserId(userId);
            reaction.setUserName("User " + userId); // TODO: Get from UserService
            reaction.setEmoji(request.getEmoji());
            reaction.setCreatedAt(LocalDateTime.now());
            message.getReactions().add(reaction);
        }

        message.setUpdatedAt(LocalDateTime.now());
        Message updated = messageRepository.save(message);

        MessageResponse response = convertToResponse(updated);

        // Gửi cập nhật real-time
        webSocketService.sendReactionUpdateToConversation(message.getConversationId(), response);

        return response;
    }

    public void removeReaction(String messageId, String userId) {
        Message message = messageRepository.findById(messageId)
            .orElseThrow(() -> new ChatException("Không tìm thấy tin nhắn"));

        message.getReactions().removeIf(r -> r.getUserId().equals(userId));
        message.setUpdatedAt(LocalDateTime.now());

        Message updated = messageRepository.save(message);
        MessageResponse response = convertToResponse(updated);

        // Gửi cập nhật real-time
        webSocketService.sendReactionUpdateToConversation(message.getConversationId(), response);
    }

    public List<MessageResponse> searchMessages(String conversationId, String query, String userId) {
        // Kiểm tra quyền truy cập
        Conversation conversation = conversationRepository.findById(conversationId)
            .orElseThrow(() -> new ChatException("Không tìm thấy cuộc trò chuyện"));

        if (!conversation.getParticipantIds().contains(userId)) {
            throw new ChatException("Bạn không có quyền tìm kiếm trong cuộc trò chuyện này");
        }

        List<Message> messages = messageRepository.searchMessagesInConversation(conversationId, query);

        return messages.stream()
            .map(this::convertToResponse)
            .collect(Collectors.toList());
    }

    public long getUnreadCount(String conversationId, String userId) {
        return messageRepository.countUnreadMessages(conversationId, userId);
    }

    public void markMessagesAsRead(String conversationId, String userId) {
        // Lấy tất cả messages chưa đọc trong conversation
        List<Message> unreadMessages = messageRepository.findByConversationIdAndIsDeletedFalseOrderByCreatedAtDesc(
            conversationId, PageRequest.of(0, 100)).getContent().stream()
            .filter(m -> !m.getReadByUserIds().contains(userId) && !m.getSenderId().equals(userId))
            .collect(Collectors.toList());

        // Đánh dấu là đã đọc
        for (Message message : unreadMessages) {
            if (!message.getReadByUserIds().contains(userId)) {
                message.getReadByUserIds().add(userId);
                message.setStatus(Message.MessageStatus.READ);
                message.setUpdatedAt(LocalDateTime.now());
            }
        }

        if (!unreadMessages.isEmpty()) {
            messageRepository.saveAll(unreadMessages);

            // Gửi read receipt real-time
            webSocketService.sendReadReceiptToConversation(conversationId, userId);
        }
    }

    private MessageResponse convertToResponse(Message message) {
        MessageResponse response = new MessageResponse();
        response.setId(message.getId());
        response.setConversationId(message.getConversationId());
        response.setSenderId(message.getSenderId());
        response.setSenderName(message.getSenderName());
        response.setSenderAvatar(message.getSenderAvatar());
        response.setType(message.getType());
        response.setContent(message.getContent());
        response.setReplyToMessageId(message.getReplyToMessageId());
        response.setStatus(message.getStatus());
        response.setReadByUserIds(message.getReadByUserIds());
        response.setCreatedAt(message.getCreatedAt());
        response.setUpdatedAt(message.getUpdatedAt());
        response.setEditedAt(message.getEditedAt());
        response.setEdited(message.isEdited());
        response.setDeleted(message.isDeleted());

        // Convert reactions
        if (message.getReactions() != null) {
            response.setReactions(
                message.getReactions().stream()
                    .map(r -> new MessageReactionResponse(r.getUserId(), r.getUserName(), r.getEmoji(), r.getCreatedAt()))
                    .collect(Collectors.toList())
            );
        }

        // Convert attachment if exists
        if (message.getAttachment() != null) {
            MessageAttachmentResponse attachment = new MessageAttachmentResponse();
            attachment.setFileName(message.getAttachment().getFileName());
            attachment.setFileUrl(message.getAttachment().getFileUrl());
            attachment.setFileType(message.getAttachment().getFileType());
            attachment.setFileSize(message.getAttachment().getFileSize());
            attachment.setThumbnailUrl(message.getAttachment().getThumbnailUrl());
            response.setAttachment(attachment);
        }

        // Load reply message if exists
        if (message.getReplyToMessageId() != null) {
            messageRepository.findById(message.getReplyToMessageId())
                .ifPresent(replyMessage -> response.setReplyToMessage(convertToResponse(replyMessage)));
        }

        return response;
    }
}

package vn.ctu.edu.chatservice.service;

import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.kafka.core.KafkaTemplate;
import org.springframework.stereotype.Service;
import vn.ctu.edu.chatservice.model.Conversation;
import vn.ctu.edu.chatservice.model.Message;

import java.util.HashMap;
import java.util.Map;

@Service
@RequiredArgsConstructor
@Slf4j
public class NotificationService {

    private final KafkaTemplate<String, Object> kafkaTemplate;
    private final WebSocketService webSocketService;

    private static final String NOTIFICATION_TOPIC = "chat_notifications";

    public void sendMessageNotification(Conversation conversation, Message message) {
        // Tạo notification event
        Map<String, Object> notificationEvent = new HashMap<>();
        notificationEvent.put("type", "NEW_MESSAGE");
        notificationEvent.put("conversationId", conversation.getId());
        notificationEvent.put("messageId", message.getId());
        notificationEvent.put("senderId", message.getSenderId());
        notificationEvent.put("senderName", message.getSenderName());
        notificationEvent.put("content", message.getContent());
        notificationEvent.put("conversationName", conversation.getName());
        notificationEvent.put("conversationType", conversation.getType().toString());

        // Gửi notification cho từng participant (trừ sender)
        conversation.getParticipantIds().stream()
            .filter(participantId -> !participantId.equals(message.getSenderId()))
            .forEach(participantId -> {
                notificationEvent.put("recipientId", participantId);

                // Gửi qua Kafka để notification service xử lý
                kafkaTemplate.send(NOTIFICATION_TOPIC, participantId, notificationEvent);

                // Gửi real-time notification qua WebSocket
                webSocketService.sendNotificationToUser(participantId, notificationEvent);

                log.debug("Sent message notification to user: {}", participantId);
            });
    }

    public void sendConversationUpdateNotification(Conversation conversation, String updateType, String updatedBy) {
        Map<String, Object> notificationEvent = new HashMap<>();
        notificationEvent.put("type", "CONVERSATION_UPDATE");
        notificationEvent.put("conversationId", conversation.getId());
        notificationEvent.put("updateType", updateType); // MEMBER_ADDED, MEMBER_REMOVED, INFO_UPDATED
        notificationEvent.put("updatedBy", updatedBy);
        notificationEvent.put("conversationName", conversation.getName());

        // Gửi cho tất cả participants
        conversation.getParticipantIds().forEach(participantId -> {
            notificationEvent.put("recipientId", participantId);
            kafkaTemplate.send(NOTIFICATION_TOPIC, participantId, notificationEvent);
            webSocketService.sendNotificationToUser(participantId, notificationEvent);
        });
    }
}

package vn.ctu.edu.chatservice.service;

import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.data.redis.core.RedisTemplate;
import org.springframework.stereotype.Service;
import vn.ctu.edu.chatservice.dto.response.UserPresenceResponse;
import vn.ctu.edu.chatservice.model.UserPresence;
import vn.ctu.edu.chatservice.repository.UserPresenceRepository;

import java.time.LocalDateTime;
import java.util.List;
import java.util.Optional;
import java.util.concurrent.TimeUnit;
import java.util.stream.Collectors;

@Service
@RequiredArgsConstructor
@Slf4j
public class UserPresenceService {

    private final UserPresenceRepository userPresenceRepository;
    private final RedisTemplate<String, Object> redisTemplate;
    private final WebSocketService webSocketService;

    private static final String PRESENCE_CACHE_PREFIX = "presence:";
    private static final String TYPING_CACHE_PREFIX = "typing:";

    public void setUserOnline(String userId, String sessionId) {
        log.info("Setting user {} online with session {}", userId, sessionId);

        UserPresence presence = userPresenceRepository.findByUserId(userId)
            .orElse(new UserPresence());

        presence.setUserId(userId);
        presence.setStatus(UserPresence.PresenceStatus.ONLINE);
        presence.setSessionId(sessionId);
        presence.setLastSeenAt(LocalDateTime.now());
        presence.setUpdatedAt(LocalDateTime.now());

        // TODO: Lấy thông tin user từ UserService
        presence.setUserName("User " + userId);
        presence.setUserAvatar("");

        userPresenceRepository.save(presence);

        // Cache trong Redis
        cacheUserPresence(presence);

        // Broadcast presence update
        webSocketService.broadcastPresenceUpdate(convertToResponse(presence));
    }

    public void setUserOffline(String userId) {
        log.info("Setting user {} offline", userId);

        Optional<UserPresence> existingPresence = userPresenceRepository.findByUserId(userId);
        if (existingPresence.isPresent()) {
            UserPresence presence = existingPresence.get();
            presence.setStatus(UserPresence.PresenceStatus.OFFLINE);
            presence.setLastSeenAt(LocalDateTime.now());
            presence.setUpdatedAt(LocalDateTime.now());
            presence.setCurrentActivity(null);
            presence.setSessionId(null);

            userPresenceRepository.save(presence);

            // Cache trong Redis
            cacheUserPresence(presence);

            // Clear typing status
            clearTypingStatus(userId);

            // Broadcast presence update
            webSocketService.broadcastPresenceUpdate(convertToResponse(presence));
        }
    }

    public void setUserAway(String userId) {
        Optional<UserPresence> existingPresence = userPresenceRepository.findByUserId(userId);
        if (existingPresence.isPresent()) {
            UserPresence presence = existingPresence.get();
            presence.setStatus(UserPresence.PresenceStatus.AWAY);
            presence.setUpdatedAt(LocalDateTime.now());

            userPresenceRepository.save(presence);
            cacheUserPresence(presence);

            webSocketService.broadcastPresenceUpdate(convertToResponse(presence));
        }
    }

    public void setTypingStatus(String userId, String conversationId, boolean isTyping) {
        String typingKey = TYPING_CACHE_PREFIX + conversationId + ":" + userId;

        if (isTyping) {
            // Set typing với TTL 10 giây
            redisTemplate.opsForValue().set(typingKey, userId, 10, TimeUnit.SECONDS);

            // Update presence activity
            Optional<UserPresence> presence = userPresenceRepository.findByUserId(userId);
            if (presence.isPresent()) {
                presence.get().setCurrentActivity("typing in " + conversationId);
                presence.get().setUpdatedAt(LocalDateTime.now());
                userPresenceRepository.save(presence.get());
            }

            log.debug("User {} started typing in conversation {}", userId, conversationId);
        } else {
            // Remove typing status
            redisTemplate.delete(typingKey);

            // Clear presence activity
            Optional<UserPresence> presence = userPresenceRepository.findByUserId(userId);
            if (presence.isPresent()) {
                presence.get().setCurrentActivity(null);
                presence.get().setUpdatedAt(LocalDateTime.now());
                userPresenceRepository.save(presence.get());
            }

            log.debug("User {} stopped typing in conversation {}", userId, conversationId);
        }

        // Broadcast typing status
        webSocketService.broadcastTypingStatus(conversationId, userId, isTyping);
    }

    public List<String> getTypingUsers(String conversationId) {
        String pattern = TYPING_CACHE_PREFIX + conversationId + ":*";
        return redisTemplate.keys(pattern).stream()
            .map(key -> (String) redisTemplate.opsForValue().get(key))
            .collect(Collectors.toList());
    }

    public UserPresenceResponse getUserPresence(String userId) {
        // Thử cache trước
        UserPresence cached = getCachedUserPresence(userId);
        if (cached != null) {
            return convertToResponse(cached);
        }

        // Nếu không có cache, query database
        Optional<UserPresence> presence = userPresenceRepository.findByUserId(userId);
        if (presence.isPresent()) {
            cacheUserPresence(presence.get());
            return convertToResponse(presence.get());
        }

        // Nếu không tìm thấy, tạo presence mặc định
        return createDefaultPresence(userId);
    }

    public List<UserPresenceResponse> getMultipleUserPresence(List<String> userIds) {
        return userIds.stream()
            .map(this::getUserPresence)
            .collect(Collectors.toList());
    }

    public List<UserPresenceResponse> getOnlineUsers() {
        List<UserPresence> onlineUsers = userPresenceRepository.findByStatus(UserPresence.PresenceStatus.ONLINE);
        return onlineUsers.stream()
            .map(this::convertToResponse)
            .collect(Collectors.toList());
    }

    public void cleanupStalePresence() {
        // Cleanup presence cũ hơn 1 giờ
        LocalDateTime oneHourAgo = LocalDateTime.now().minusHours(1);
        userPresenceRepository.deleteByLastSeenAtBefore(oneHourAgo);

        log.info("Cleaned up stale presence records");
    }

    private void cacheUserPresence(UserPresence presence) {
        String cacheKey = PRESENCE_CACHE_PREFIX + presence.getUserId();
        redisTemplate.opsForValue().set(cacheKey, presence, 5, TimeUnit.MINUTES);
    }

    private UserPresence getCachedUserPresence(String userId) {
        String cacheKey = PRESENCE_CACHE_PREFIX + userId;
        return (UserPresence) redisTemplate.opsForValue().get(cacheKey);
    }

    private void clearTypingStatus(String userId) {
        String pattern = TYPING_CACHE_PREFIX + "*:" + userId;
        redisTemplate.keys(pattern).forEach(redisTemplate::delete);
    }

    private UserPresenceResponse createDefaultPresence(String userId) {
        UserPresenceResponse response = new UserPresenceResponse();
        response.setUserId(userId);
        response.setUserName("User " + userId);
        response.setUserAvatar("");
        response.setStatus(UserPresence.PresenceStatus.OFFLINE);
        response.setLastSeenAt(LocalDateTime.now());
        return response;
    }

    private UserPresenceResponse convertToResponse(UserPresence presence) {
        UserPresenceResponse response = new UserPresenceResponse();
        response.setUserId(presence.getUserId());
        response.setUserName(presence.getUserName());
        response.setUserAvatar(presence.getUserAvatar());
        response.setStatus(presence.getStatus());
        response.setCurrentActivity(presence.getCurrentActivity());
        response.setLastSeenAt(presence.getLastSeenAt());
        return response;
    }
}

package vn.ctu.edu.chatservice.service;

import lombok.extern.slf4j.Slf4j;
import org.springframework.beans.factory.annotation.Value;
import org.springframework.stereotype.Service;
import org.springframework.web.client.RestTemplate;

import java.util.Map;

@Service
@Slf4j
public class UserService {

    private final RestTemplate restTemplate;
    private final String userServiceUrl;

    public UserService(RestTemplate restTemplate, 
                      @Value("${user.service.url:http://user-service:8081}") String userServiceUrl) {
        this.restTemplate = restTemplate;
        this.userServiceUrl = userServiceUrl;
    }

    public Map<String, Object> getUserInfo(String userId) {
        try {
            String url = userServiceUrl + "/api/users/" + userId;
            return restTemplate.getForObject(url, Map.class);
        } catch (Exception e) {
            log.error("Failed to get user info for userId: {}", userId, e);
            return createDefaultUserInfo(userId);
        }
    }

    private Map<String, Object> createDefaultUserInfo(String userId) {
        return Map.of(
            "id", userId,
            "name", "User " + userId,
            "avatar", ""
        );
    }
}

package vn.ctu.edu.chatservice.service;

import lombok.RequiredArgsConstructor;
import lombok.extern.slf4j.Slf4j;
import org.springframework.messaging.simp.SimpMessagingTemplate;
import org.springframework.stereotype.Service;
import vn.ctu.edu.chatservice.dto.response.MessageResponse;
import vn.ctu.edu.chatservice.dto.response.UserPresenceResponse;

@Service
@RequiredArgsConstructor
@Slf4j
public class WebSocketService {

    private final SimpMessagingTemplate messagingTemplate;

    // Send message to all users in a conversation
    public void sendMessageToConversation(String conversationId, MessageResponse message) {
        String destination = "/topic/conversation/" + conversationId + "/messages";
        messagingTemplate.convertAndSend(destination, message);
        log.debug("Sent message to conversation {}: {}", conversationId, message.getId());
    }

    // Send message update (edit) to conversation
    public void sendMessageUpdateToConversation(String conversationId, MessageResponse message) {
        String destination = "/topic/conversation/" + conversationId + "/messages/update";
        messagingTemplate.convertAndSend(destination, message);
        log.debug("Sent message update to conversation {}: {}", conversationId, message.getId());
    }

    // Send message delete notification
    public void sendMessageDeleteToConversation(String conversationId, String messageId) {
        String destination = "/topic/conversation/" + conversationId + "/messages/delete";
        messagingTemplate.convertAndSend(destination, messageId);
        log.debug("Sent message delete to conversation {}: {}", conversationId, messageId);
    }

    // Send reaction update
    public void sendReactionUpdateToConversation(String conversationId, MessageResponse message) {
        String destination = "/topic/conversation/" + conversationId + "/reactions";
        messagingTemplate.convertAndSend(destination, message);
        log.debug("Sent reaction update to conversation {}: {}", conversationId, message.getId());
    }

    // Send typing status
    public void broadcastTypingStatus(String conversationId, String userId, boolean isTyping) {
        String destination = "/topic/conversation/" + conversationId + "/typing";
        TypingEvent event = new TypingEvent(userId, isTyping);
        messagingTemplate.convertAndSend(destination, event);
        log.debug("Broadcasted typing status for user {} in conversation {}: {}", userId, conversationId, isTyping);
    }

    // Send read receipt
    public void sendReadReceiptToConversation(String conversationId, String userId) {
        String destination = "/topic/conversation/" + conversationId + "/read";
        ReadReceiptEvent event = new ReadReceiptEvent(userId);
        messagingTemplate.convertAndSend(destination, event);
        log.debug("Sent read receipt for user {} in conversation {}", userId, conversationId);
    }

    // Broadcast user presence update
    public void broadcastPresenceUpdate(UserPresenceResponse presence) {
        String destination = "/topic/presence";
        messagingTemplate.convertAndSend(destination, presence);
        log.debug("Broadcasted presence update for user {}: {}", presence.getUserId(), presence.getStatus());
    }

    // Send notification to specific user
    public void sendNotificationToUser(String userId, Object notification) {
        String destination = "/queue/user/" + userId + "/notifications";
        messagingTemplate.convertAndSend(destination, notification);
        log.debug("Sent notification to user {}", userId);
    }

    // Send conversation update (new conversation, member added/removed)
    public void sendConversationUpdate(String conversationId, Object update) {
        String destination = "/topic/conversation/" + conversationId + "/updates";
        messagingTemplate.convertAndSend(destination, update);
        log.debug("Sent conversation update to {}", conversationId);
    }

    // Event classes
    public static class TypingEvent {
        private String userId;
        private boolean isTyping;

        public TypingEvent(String userId, boolean isTyping) {
            this.userId = userId;
            this.isTyping = isTyping;
        }

        // Getters and setters
        public String getUserId() { return userId; }
        public void setUserId(String userId) { this.userId = userId; }
        public boolean isTyping() { return isTyping; }
        public void setTyping(boolean typing) { isTyping = typing; }
    }

    public static class ReadReceiptEvent {
        private String userId;

        public ReadReceiptEvent(String userId) {
            this.userId = userId;
        }

        // Getters and setters
        public String getUserId() { return userId; }
        public void setUserId(String userId) { this.userId = userId; }
    }
}

'use client';

import React from 'react';
import { useAuth } from '@/contexts/AuthContext';
import Layout from '@/components/layout/Layout';
import Card from '@/components/ui/Card';
import { useRouter } from 'next/navigation';
import { useEffect } from 'react';

export default function MessagesPage() {
  const { user, loading } = useAuth();
  const router = useRouter();

  useEffect(() => {
    if (!loading && !user) {
      router.push('/login');
    }
  }, [user, loading, router]);

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-indigo-600"></div>
      </div>
    );
  }

  if (!user) {
    return null;
  }

  return (
    <Layout>
      <div className="max-w-6xl mx-auto">
        <Card className="min-h-[600px]">
          <div className="text-center py-16">
            <h1 className="text-2xl font-bold text-gray-900 mb-4">Tin nhắn</h1>
            <p className="text-gray-600 mb-8">Tính năng chat sẽ sớm được phát triển</p>
            <div className="bg-gray-50 rounded-lg p-8">
              <p className="text-sm text-gray-500">
                Chức năng nhắn tin đang được phát triển và sẽ có sẵn trong phiên bản tiếp theo.
              </p>
            </div>
          </div>
        </Card>
      </div>
    </Layout>
  );
}

