export const ProfileHeader: React.FC<ProfileHeaderProps> = ({
  user,
  isOwnProfile,
  isFollowing = false,
  onFollow,
  onMessage,
  onEditProfile,
  onEditCover,
  onEditAvatar
}) => {
  const [showFullBio, setShowFullBio] = useState(false);

  const getRoleDisplay = (role: string) => {
    switch (role) {
      case 'STUDENT': return 'Sinh viên';
      case 'LECTURER': return 'Giảng viên';
      case 'ADMIN': return 'Quản trị viên';
      default: return 'Người dùng';
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-sm overflow-hidden">
      {/* Cover Photo */}
      <div className="relative h-80 bg-gradient-to-r from-blue-500 to-purple-600">
        {user.backgroundUrl ? (
          <img
            src={user.backgroundUrl}
            alt="Ảnh bìa"
            className="w-full h-full object-cover"
          />
        ) : (
          <div className="w-full h-full bg-gradient-to-r from-blue-500 via-purple-500 to-pink-500" />
        )}

        {/* Cover Photo Edit Button */}
        {isOwnProfile && (
          <button
            onClick={onEditCover}
            className="absolute bottom-4 right-4 bg-white bg-opacity-90 hover:bg-opacity-100 rounded-lg px-3 py-2 text-gray-700 font-medium transition-all duration-200 flex items-center space-x-2"
          >
            <Camera className="h-4 w-4" />
            <span className="text-sm">Chỉnh sửa ảnh bìa</span>
          </button>
        )}
      </div>

      {/* Profile Info Section */}
      <div className="px-6 pb-6">
        <div className="flex flex-col lg:flex-row lg:items-end lg:justify-between -mt-20 relative">
          {/* Avatar and Basic Info */}
          <div className="flex flex-col sm:flex-row sm:items-end sm:space-x-5">
            {/* Avatar */}
            <div className="relative">
              <div className="w-40 h-40 rounded-full border-4 border-white shadow-xl overflow-hidden bg-gray-200">
                {user.avatarUrl ? (
                  <img
                    src={user.avatarUrl}
                    alt={user.fullName || user.name || 'Avatar'}
                    className="w-full h-full object-cover"
                  />
                ) : (
                  <div className="w-full h-full bg-gradient-to-br from-blue-400 to-purple-600 flex items-center justify-center text-white text-4xl font-bold">
                    {(user.fullName || user.name || 'U').charAt(0).toUpperCase()}
                  </div>
                )}
              </div>

              {/* Avatar Edit Button */}
              {isOwnProfile && (
                <button
                  onClick={onEditAvatar}
                  className="absolute bottom-2 right-2 bg-gray-100 hover:bg-gray-200 rounded-full p-2 shadow-lg transition-colors"
                >
                  <Camera className="h-4 w-4 text-gray-600" />
                </button>
              )}
            </div>

            {/* Name and Title */}
            <div className="mt-4 sm:mt-0 flex-1">
              <div className="flex items-center space-x-2">
                <h1 className="text-3xl font-bold text-gray-900 vietnamese-text">
                  {user.fullName || user.name || 'Người dùng'}
                </h1>
                {user.isVerified && (
                  <div className="w-6 h-6 bg-blue-500 rounded-full flex items-center justify-center">
                    <svg className="w-4 h-4 text-white" fill="currentColor" viewBox="0 0 20 20">
                      <path fillRule="evenodd" d="M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L8 12.586l7.293-7.293a1 1 0 011.414 0z" clipRule="evenodd" />
                    </svg>
                  </div>
                )}
              </div>

              <div className="flex items-center space-x-2 mt-1">
                <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                  user.role === 'LECTURER' 
                    ? 'bg-blue-100 text-blue-700' 
                    : 'bg-green-100 text-green-700'
                }`}>
                  {getRoleDisplay(user.role)}
                </span>
                {user.isOnline && (
                  <span className="flex items-center text-sm text-gray-500">
                    <div className="w-2 h-2 bg-green-500 rounded-full mr-1"></div>
                    Đang hoạt động
                  </span>
                )}
              </div>

              {/* Quick Info */}
              <div className="flex flex-wrap items-center mt-3 text-sm text-gray-600 space-x-4">
                {user.role === 'STUDENT' && user.major && (
                  <div className="flex items-center space-x-1">
                    <GraduationCap className="h-4 w-4" />
                    <span>{user.major.name}</span>
                  </div>
                )}

                {user.role === 'LECTURER' && user.faculty && (
                  <div className="flex items-center space-x-1">
                    <Briefcase className="h-4 w-4" />
                    <span>{user.faculty.name}</span>
                  </div>
                )}

                {user.createdAt && (
                  <div className="flex items-center space-x-1">
                    <Calendar className="h-4 w-4" />
                    <span>Tham gia {formatTimeAgo(user.createdAt)}</span>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex items-center space-x-3 mt-4 lg:mt-0">
            {isOwnProfile ? (
              <>
                <Button
                  onClick={onEditProfile}
                  variant="outline"
                  className="flex items-center space-x-2"
                >
                  <Edit3 className="h-4 w-4" />
                  <span>Chỉnh sửa trang cá nhân</span>
                </Button>
              </>
            ) : (
              <>
                <Button
                  onClick={onFollow}
                  variant={isFollowing ? "outline" : "primary"}
                  className="flex items-center space-x-2"
                >
                  <UserPlus className="h-4 w-4" />
                  <span>{isFollowing ? 'Đang theo dõi' : 'Theo dõi'}</span>
                </Button>

                <Button
                  onClick={onMessage}
                  variant="outline"
                  className="flex items-center space-x-2"
                >
                  <MessageCircle className="h-4 w-4" />
                  <span>Nhắn tin</span>
                </Button>

                <button className="p-2 hover:bg-gray-100 rounded-lg transition-colors">
                  <MoreHorizontal className="h-5 w-5 text-gray-600" />
                </button>
              </>
            )}
          </div>
        </div>

        {/* Bio Section */}
        {user.bio && (
          <div className="mt-6 bg-gray-50 rounded-lg p-4">
            <h3 className="font-semibold text-gray-900 mb-2">Giới thiệu</h3>
            <p className="text-gray-700 vietnamese-text leading-relaxed">
              {showFullBio || user.bio.length <= 200 
                ? user.bio 
                : `${user.bio.substring(0, 200)}...`
              }
            </p>
            {user.bio.length > 200 && (
              <button
                onClick={() => setShowFullBio(!showFullBio)}
                className="text-blue-600 hover:text-blue-700 font-medium mt-2"
              >
                {showFullBio ? 'Thu gọn' : 'Xem thêm'}
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

'use client';

import React, { useState } from 'react';
import { User } from '@/types';
import { Camera, X, Upload, Loader2 } from 'lucide-react';
import { Button } from '@/components/ui/Button';
import { Modal } from '@/components/ui/Modal';

interface ProfilePhotoModalProps {
  isOpen: boolean;
  onClose: () => void;
  user: User;
  type: 'avatar' | 'cover';
  onPhotoUpdate: (photoUrl: string) => void;
}

export const ProfilePhotoModal: React.FC<ProfilePhotoModalProps> = ({
  isOpen,
  onClose,
  user,
  type,
  onPhotoUpdate
}) => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [previewUrl, setPreviewUrl] = useState<string | null>(null);
  const [isUploading, setIsUploading] = useState(false);

  const handleFileSelect = (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (file) {
      setSelectedFile(file);
      const url = URL.createObjectURL(file);
      setPreviewUrl(url);
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) return;

    setIsUploading(true);
    try {
      // TODO: Implement actual upload logic
      // const uploadResult = await mediaService.uploadImage(selectedFile);
      // onPhotoUpdate(uploadResult.url);

      // Mock upload for now
      setTimeout(() => {
        onPhotoUpdate(previewUrl || '');
        setIsUploading(false);
        onClose();
        resetModal();
      }, 2000);
    } catch (error) {
      console.error('Error uploading photo:', error);
      setIsUploading(false);
    }
  };

  const resetModal = () => {
    setSelectedFile(null);
    if (previewUrl) {
      URL.revokeObjectURL(previewUrl);
    }
    setPreviewUrl(null);
  };

  const handleClose = () => {
    if (!isUploading) {
      resetModal();
      onClose();
    }
  };

  const title = type === 'avatar' ? 'Cập nhật ảnh đại diện' : 'Cập nhật ảnh bìa';
  const aspectRatio = type === 'avatar' ? 'aspect-square' : 'aspect-[3/1]';

  return (
    <Modal isOpen={isOpen} onClose={handleClose} title={title}>
      <div className="space-y-6">
        {/* Current Photo */}
        <div className="text-center">
          <h3 className="text-sm font-medium text-gray-900 mb-3 vietnamese-text">
            {type === 'avatar' ? 'Ảnh đại diện hiện tại' : 'Ảnh bìa hiện tại'}
          </h3>
          <div className={`mx-auto bg-gray-200 rounded-lg overflow-hidden ${
            type === 'avatar' ? 'w-32 h-32 rounded-full' : 'w-full h-40'
          }`}>
            {(type === 'avatar' ? user.avatarUrl : user.backgroundUrl) ? (
              <img
                src={type === 'avatar' ? user.avatarUrl! : user.backgroundUrl!}
                alt={title}
                className="w-full h-full object-cover"
              />
            ) : (
              <div className="w-full h-full bg-gradient-to-br from-blue-400 to-purple-600 flex items-center justify-center text-white text-2xl font-bold">
                {type === 'avatar' ? (user.fullName || user.name || 'U').charAt(0).toUpperCase() : ''}
              </div>
            )}
          </div>
        </div>

        {/* Preview New Photo */}
        {previewUrl && (
          <div className="text-center">
            <h3 className="text-sm font-medium text-gray-900 mb-3 vietnamese-text">
              Xem trước
            </h3>
            <div className={`mx-auto bg-gray-200 rounded-lg overflow-hidden ${
              type === 'avatar' ? 'w-32 h-32 rounded-full' : 'w-full h-40'
            }`}>
              <img
                src={previewUrl}
                alt="Preview"
                className="w-full h-full object-cover"
              />
            </div>
          </div>
        )}

        {/* File Upload */}
        <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center hover:border-gray-400 transition-colors">
          <input
            type="file"
            accept="image/*"
            onChange={handleFileSelect}
            className="hidden"
            id="photo-upload"
            disabled={isUploading}
          />
          <label htmlFor="photo-upload" className="cursor-pointer">
            <Upload className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-sm text-gray-600 vietnamese-text">
              Nhấp để chọn ảnh hoặc kéo thả ảnh vào đây
            </p>
            <p className="text-xs text-gray-500 mt-1 vietnamese-text">
              Định dạng: JPG, PNG (Tối đa 10MB)
            </p>
          </label>
        </div>

        {/* Action Buttons */}
        <div className="flex justify-end space-x-3">
          <Button
            variant="outline"
            onClick={handleClose}
            disabled={isUploading}
          >
            Hủy
          </Button>
          <Button
            onClick={handleUpload}
            disabled={!selectedFile || isUploading}
            className="flex items-center space-x-2"
          >
            {isUploading ? (
              <>
                <Loader2 className="h-4 w-4 animate-spin" />
                <span>Đang tải lên...</span>
              </>
            ) : (
              <>
                <Camera className="h-4 w-4" />
                <span>Cập nhật ảnh</span>
              </>
            )}
          </Button>
        </div>
      </div>
    </Modal>
  );
};

'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { Post, PaginatedResponse } from '@/types';
import { postService } from '@/services/postService';
import { PostCard } from '@/components/post/PostCard';
import { LoadingSpinner } from '@/components/ui/LoadingSpinner';
import { ErrorAlert } from '@/components/ui/ErrorAlert';
import { Button } from '@/components/ui/Button';
import { RefreshCw, FileText, Image, Video, Clock } from 'lucide-react';
import { useAuth } from '@/contexts/AuthContext';

interface ProfilePostFeedProps {
  userId: string;
  userName?: string;
  isOwnProfile?: boolean;
  className?: string;
}

export const ProfilePostFeed: React.FC<ProfilePostFeedProps> = ({
  userId,
  userName,
  isOwnProfile = false,
  className = ''
}) => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isLoadingMore, setIsLoadingMore] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [hasMore, setHasMore] = useState(true);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPosts, setTotalPosts] = useState(0);
  const [activeFilter, setActiveFilter] = useState<'all' | 'text' | 'image' | 'video'>('all');
  const {user} = useAuth();
  const loadUserPosts = useCallback(async (page = 0, append = false, filter = 'all') => {
    try {
      if (!append) {
        setIsLoading(true);
        setError(null);
      } else {
        setIsLoadingMore(true);
      }
      let response;
      // TODO: Replace with actual API call to get user posts
      if (userId !== user?.id) {
        response = await postService.getUserPosts(userId, page, 10, filter);
      } else {
      }
      }

        const mockResponse: PaginatedResponse<Post> = response;
      // Mock data for now - replace with actual API call
      /*const mockResponse: PaginatedResponse<Post> = {
        content: [],
        totalElements: 0,
        totalPages: 0,
        size: 10,
        number: page,
        first: page === 0,
        last: true
      };*/
        setPosts(mockResponse.content);
      }

        setPosts(prev => [...prev, ...mockResponse.content]);
      setHasMore(!mockResponse.last);
      setCurrentPage(mockResponse.number);

    } catch (err: any) {
        setPosts(mockResponse.content);
      setError('Không thể tải bài viết của người dùng');
    } finally {
      setTotalPosts(mockResponse.totalElements);
      setHasMore(!mockResponse.last);
      setCurrentPage(mockResponse.number);
  }, [userId]);

  useEffect(() => {
    loadUserPosts(0, false, activeFilter);
  }, [loadUserPosts, activeFilter]);

  const handleLoadMore = () => {
    if (hasMore && !isLoadingMore) {
  }, [userId]);
    }
  };

  const handleRefresh = () => {
    loadUserPosts(0, false, activeFilter);
  };

  const handleFilterChange = (filter: 'all' | 'text' | 'image' | 'video') => {
    setActiveFilter(filter);
    setCurrentPage(0);
  };

  const handlePostUpdate = (updatedPost: Post) => {
    setPosts(prev => prev.map(post =>
      post.id === updatedPost.id ? updatedPost : post
    ));
  };

  const handlePostDelete = (postId: string) => {
    setPosts(prev => prev.filter(post => post.id !== postId));
    setTotalPosts(prev => prev - 1);
  };

  const getFilterIcon = (filter: string) => {
    switch (filter) {
      case 'text': return <FileText className="h-4 w-4" />;
      case 'image': return <Image className="h-4 w-4" />;
      case 'video': return <Video className="h-4 w-4" />;
      default: return <Clock className="h-4 w-4" />;
    }
  };

  const getFilterLabel = (filter: string) => {
    switch (filter) {
      case 'text': return 'Văn bản';
      case 'image': return 'Hình ảnh';
      case 'video': return 'Video';
      default: return 'Tất cả';
    }
  };

  if (isLoading) {
    return (
      <div className={`space-y-6 ${className}`}>
        <div className="flex justify-center py-12">
          <div className="text-center">
            <LoadingSpinner size="lg" />
            <p className="text-gray-600 mt-4 vietnamese-text">
              Đang tải bài viết...
            </p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`space-y-6 ${className}`}>
        <ErrorAlert
          message={error}
          onRetry={handleRefresh}
        />
      </div>
    );
  }

  return (
    <div className={`space-y-6 ${className}`}>
      {/* Header with Stats and Filters */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
          {/* Post Count */}
          <div>
            <h2 className="text-xl font-bold text-gray-900 vietnamese-text">
              Bài viết của {isOwnProfile ? 'bạn' : (userName || 'người dùng')}
            </h2>
            <p className="text-sm text-gray-600 vietnamese-text">
              {totalPosts} bài viết
            </p>
          </div>

          {/* Refresh Button */}
          <Button
            variant="outline"
            onClick={handleRefresh}
            className="flex items-center space-x-2"
            disabled={isLoading}
          >
            <RefreshCw className={`h-4 w-4 ${isLoading ? 'animate-spin' : ''}`} />
            <span className="vietnamese-text">Làm mới</span>
          </Button>
        </div>

        {/* Filter Tabs */}
        <div className="mt-6 border-b border-gray-200">
          <nav className="flex space-x-8">
            {[
              { key: 'all', label: 'Tất cả' },
              { key: 'text', label: 'Văn bản' },
              { key: 'image', label: 'Hình ảnh' },
              { key: 'video', label: 'Video' }
            ].map((filter) => (
              <button
                key={filter.key}
                onClick={() => handleFilterChange(filter.key as any)}
                className={`py-2 px-1 border-b-2 font-medium text-sm vietnamese-text transition-colors flex items-center space-x-2 ${
                  activeFilter === filter.key
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {getFilterIcon(filter.key)}
                <span>{filter.label}</span>
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Posts List */}
      {posts.length === 0 ? (
        <div className="bg-white rounded-lg shadow-sm p-12 text-center">
          <div className="text-gray-400 mb-4">
            <FileText className="h-16 w-16 mx-auto" />
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2 vietnamese-text">
            {isOwnProfile ? 'Bạn chưa có bài viết nào' : 'Người dùng này chưa có bài viết nào'}
          </h3>
          <p className="text-gray-600 vietnamese-text">
            {isOwnProfile
              ? 'Hãy tạo bài viết đầu tiên để chia sẻ với mọi người!'
              : 'Hãy quay lại sau để xem bài viết mới nhất.'
            }
          </p>
          {isOwnProfile && (
            <Button
              className="mt-4"
              onClick={() => window.location.href = '/posts/create'}
            >
              Tạo bài viết đầu tiên
            </Button>
          )}
        </div>
      ) : (
        <>
          {/* Posts */}
          <div className="space-y-6">
            {posts.map((post) => (
              <PostCard
                key={post.id}
                post={post}
                onPostUpdate={handlePostUpdate}
                onPostDelete={handlePostDelete}
                className="shadow-sm hover:shadow-md transition-shadow"
              />
            ))}
          </div>

          {/* Load More Button */}
          {hasMore && (
            <div className="text-center pt-6">
              <Button
                variant="outline"
                onClick={handleLoadMore}
                disabled={isLoadingMore}
                className="px-8"
              >
                {isLoadingMore ? (
                  <>
                    <LoadingSpinner size="sm" className="mr-2" />
                    <span className="vietnamese-text">Đang tải...</span>
                  </>
                ) : (
                  <span className="vietnamese-text">Xem thêm bài viết</span>
                )}
              </Button>
            </div>
          )}

          {/* End Message */}
          {!hasMore && posts.length > 0 && (
            <div className="text-center py-8">
              <p className="text-gray-500 vietnamese-text">
                Bạn đã xem hết tất cả bài viết
              </p>
            </div>
          )}
        </>
      )}
    </div>
  );
};

'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { Post, PaginatedResponse } from '@/types';
import { postService } from '@/services/postService';
import { PostCard } from '@/components/post/PostCard';
import { LoadingSpinner } from '@/components/ui/LoadingSpinner';
import { ErrorAlert } from '@/components/ui/ErrorAlert';
import { Button } from '@/components/ui/Button';
import { RefreshCw, FileText, Image, Video, Clock } from 'lucide-react';
import { useAuth } from '@/contexts/AuthContext';

interface ProfilePostFeedProps {
  userId: string;
  userName?: string;
  isOwnProfile?: boolean;
  className?: string;
}

export const ProfilePostFeed: React.FC<ProfilePostFeedProps> = ({
  userId,
  userName,
  isOwnProfile = false,
  className = ''
}) => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [isLoadingMore, setIsLoadingMore] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [hasMore, setHasMore] = useState(true);
  const [currentPage, setCurrentPage] = useState(0);
  const [totalPosts, setTotalPosts] = useState(0);
  const [activeFilter, setActiveFilter] = useState<'all' | 'text' | 'image' | 'video'>('all');
  const {user} = useAuth();
  const loadUserPosts = useCallback(async (page = 0, append = false, filter = 'all') => {
    try {
      if (!append) {
        setIsLoading(true);
        setError(null);
      } else {
        setIsLoadingMore(true);
      }

      // Call the correct API endpoint based on whether it's own profile or not
      let response: PaginatedResponse<Post>;
      
      if (isOwnProfile) {
        // For own profile, use getMyPosts which calls /posts/me
        response = await postService.getMyPosts(page, 10);
      } else {
        // For other users, we need to implement getUserPosts in postService
        // For now, let's create a temporary implementation
        const apiResponse = await fetch(`/api/posts/user/${userId}?page=${page}&size=10`, {
          headers: {
            'Authorization': `Bearer ${localStorage.getItem('token')}`,
            'Content-Type': 'application/json'
          }
        });
        
        if (!apiResponse.ok) {
          throw new Error('Failed to fetch user posts');
        }
        
        response = await apiResponse.json();
      }

      if (append) {
        setPosts(prev => [...prev, ...response.content]);
      } else {
        setPosts(response.content);
      }

      setTotalPosts(response.totalElements);
      setHasMore(!response.last);
      setCurrentPage(response.number);

    } catch (err: any) {
      console.error('Error loading user posts:', err);
      setError('Không thể tải bài viết của người dùng');
    } finally {
      setIsLoading(false);
      setIsLoadingMore(false);
    }
  }, [userId, isOwnProfile]);

  useEffect(() => {
    loadUserPosts(0, false, activeFilter);
  }, [loadUserPosts, activeFilter]);

  const handleLoadMore = () => {
    if (hasMore && !isLoadingMore) {
      loadUserPosts(currentPage + 1, true, activeFilter);
    }
  };

  const handleRefresh = () => {
    loadUserPosts(0, false, activeFilter);
  };

  const handleFilterChange = (filter: 'all' | 'text' | 'image' | 'video') => {
    setActiveFilter(filter);
    setCurrentPage(0);
  };

  const handlePostUpdate = (updatedPost: Post) => {
    setPosts(prev => prev.map(post =>
      post.id === updatedPost.id ? updatedPost : post
    ));
  };

  const handlePostDelete = (postId: string) => {
    setPosts(prev => prev.filter(post => post.id !== postId));
    setTotalPosts(prev => prev - 1);
  };

  const getFilterIcon = (filter: string) => {
    switch (filter) {
      case 'text': return <FileText className="h-4 w-4" />;
      case 'image': return <Image className="h-4 w-4" />;
      case 'video': return <Video className="h-4 w-4" />;
      default: return <Clock className="h-4 w-4" />;
    }
  };

  const getFilterLabel = (filter: string) => {
    switch (filter) {
      case 'text': return 'Văn bản';
      case 'image': return 'Hình ảnh';
      case 'video': return 'Video';
      default: return 'Tất cả';
    }
  };

  if (isLoading) {
    return (
      <div className={`space-y-6 ${className}`}>
        <div className="flex justify-center py-12">
          <div className="text-center">
            <LoadingSpinner size="lg" />
            <p className="text-gray-600 mt-4 vietnamese-text">
              Đang tải bài viết...
            </p>
          </div>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className={`space-y-6 ${className}`}>
        <ErrorAlert
          message={error}
          onRetry={handleRefresh}
        />
      </div>
    );
  }

  return (
    <div className={`space-y-6 ${className}`}>
      {/* Header with Stats and Filters */}
      <div className="bg-white rounded-lg shadow-sm p-6">
        <div className="flex flex-col sm:flex-row sm:items-center sm:justify-between gap-4">
          {/* Post Count */}
          <div>
            <h2 className="text-xl font-bold text-gray-900 vietnamese-text">
              Bài viết của {isOwnProfile ? 'bạn' : (userName || 'người dùng')}
            </h2>
            <p className="text-sm text-gray-600 vietnamese-text">
              {totalPosts} bài viết
            </p>
          </div>

          {/* Refresh Button */}
          <Button
            variant="outline"
            onClick={handleRefresh}
            className="flex items-center space-x-2"
            disabled={isLoading}
          >
            <RefreshCw className={`h-4 w-4 ${isLoading ? 'animate-spin' : ''}`} />
            <span className="vietnamese-text">Làm mới</span>
          </Button>
        </div>

        {/* Filter Tabs */}
        <div className="mt-6 border-b border-gray-200">
          <nav className="flex space-x-8">
            {[
              { key: 'all', label: 'Tất cả' },
              { key: 'text', label: 'Văn bản' },
              { key: 'image', label: 'Hình ảnh' },
              { key: 'video', label: 'Video' }
            ].map((filter) => (
              <button
                key={filter.key}
                onClick={() => handleFilterChange(filter.key as any)}
                className={`py-2 px-1 border-b-2 font-medium text-sm vietnamese-text transition-colors flex items-center space-x-2 ${
                  activeFilter === filter.key
                    ? 'border-blue-500 text-blue-600'
                    : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300'
                }`}
              >
                {getFilterIcon(filter.key)}
                <span>{filter.label}</span>
              </button>
            ))}
          </nav>
        </div>
      </div>

      {/* Posts List */}
      {posts.length === 0 ? (
        <div className="bg-white rounded-lg shadow-sm p-12 text-center">
          <div className="text-gray-400 mb-4">
            <FileText className="h-16 w-16 mx-auto" />
          </div>
          <h3 className="text-lg font-medium text-gray-900 mb-2 vietnamese-text">
            {isOwnProfile ? 'Bạn chưa có bài viết nào' : 'Người dùng này chưa có bài viết nào'}
          </h3>
          <p className="text-gray-600 vietnamese-text">
            {isOwnProfile
              ? 'Hãy tạo bài viết đầu tiên để chia sẻ với mọi người!'
              : 'Hãy quay lại sau để xem bài viết mới nhất.'
            }
          </p>
          {isOwnProfile && (
            <Button
              className="mt-4"
              onClick={() => window.location.href = '/posts/create'}
            >
              Tạo bài viết đầu tiên
            </Button>
          )}
        </div>
      ) : (
        <>
          {/* Posts */}
          <div className="space-y-6">
            {posts.map((post) => (
              <PostCard
                key={post.id}
                post={post}
                onPostUpdate={handlePostUpdate}
                onPostDelete={handlePostDelete}
                className="shadow-sm hover:shadow-md transition-shadow"
              />
            ))}
          </div>

          {/* Load More Button */}
          {hasMore && (
            <div className="text-center pt-6">
              <Button
                variant="outline"
                onClick={handleLoadMore}
                disabled={isLoadingMore}
                className="px-8"
              >
                {isLoadingMore ? (
                  <>
                    <LoadingSpinner size="sm" className="mr-2" />
                    <span className="vietnamese-text">Đang tải...</span>
                  </>
                ) : (
                  <span className="vietnamese-text">Xem thêm bài viết</span>
                )}
              </Button>
            </div>
          )}

          {/* End Message */}
          {!hasMore && posts.length > 0 && (
            <div className="text-center py-8">
              <p className="text-gray-500 vietnamese-text">
                Bạn đã xem hết tất cả bài viết
              </p>
            </div>
          )}
        </>
      )}
    </div>
  );
};

'use client';

import React from 'react';
import { Users, FileText, Heart, Eye } from 'lucide-react';

interface ProfileStatsProps {
  stats: {
    posts?: number;
    followers?: number;
    following?: number;
    likes?: number;
    views?: number;
  };
  onStatsClick?: (type: 'posts' | 'followers' | 'following') => void;
}

export const ProfileStats: React.FC<ProfileStatsProps> = ({ stats, onStatsClick }) => {
  const formatNumber = (num: number): string => {
    if (num >= 1000000) return `${(num / 1000000).toFixed(1)}M`;
    if (num >= 1000) return `${(num / 1000).toFixed(1)}K`;
    return num.toString();
  };

  const statItems = [
    {
      key: 'posts' as const,
      label: 'Bài viết',
      value: stats.posts || 0,
      icon: <FileText className="h-5 w-5 text-blue-500" />,
      clickable: true
    },
    {
      key: 'followers' as const,
      label: 'Người theo dõi',
      value: stats.followers || 0,
      icon: <Users className="h-5 w-5 text-green-500" />,
      clickable: true
    },
    {
      key: 'following' as const,
      label: 'Đang theo dõi',
      value: stats.following || 0,
      icon: <Users className="h-5 w-5 text-purple-500" />,
      clickable: true
    },
    {
      key: 'likes' as const,
      label: 'Lượt thích',
      value: stats.likes || 0,
      icon: <Heart className="h-5 w-5 text-red-500" />,
      clickable: false
    },
    {
      key: 'views' as const,
      label: 'Lượt xem',
      value: stats.views || 0,
      icon: <Eye className="h-5 w-5 text-gray-500" />,
      clickable: false
    }
  ];

  return (
    <div className="bg-white rounded-lg shadow-sm p-6">
      <h2 className="text-xl font-bold text-gray-900 mb-6 vietnamese-text">Thống kê</h2>

      <div className="grid grid-cols-2 lg:grid-cols-5 gap-4">
        {statItems.map((item) => (
          <div
            key={item.key}
            className={`text-center p-4 rounded-lg border ${
              item.clickable 
                ? 'cursor-pointer hover:bg-gray-50 hover:border-gray-300 transition-all duration-200' 
                : 'bg-gray-50'
            }`}
            onClick={item.clickable ? () => onStatsClick?.(item.key) : undefined}
          >
            <div className="flex justify-center mb-2">
              {item.icon}
            </div>
            <div className="text-2xl font-bold text-gray-900 mb-1">
              {formatNumber(item.value)}
            </div>
            <div className="text-sm text-gray-600 vietnamese-text">
              {item.label}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

