// Export singleton instance
export const chatService = new ChatService();

import { useState, useEffect, useCallback, useRef } from 'react';
import { webSocketService, TypingEvent, PresenceEvent } from '@/services/websocket.service';
import { chatService } from '@/features/chat/services/chat.service';
import { useAuth } from '@/contexts/AuthContext';
import { PaginatedResponse } from '@/shared/types';
import { ChatMessage, ChatRoom } from '@/shared/types/chat';

export interface ChatState {
  conversations: any[];
  activeConversation: string | null;
  messages: { [conversationId: string]: ChatMessage[] };
  onlineUsers: Set<string>;
  typingUsers: { [conversationId: string]: Set<string> };
  isConnected: boolean;
  isLoading: boolean;
  error: string | null;
}

export interface UseChatReturn extends ChatState {
  // Connection management
  connect: () => Promise<void>;
  disconnect: () => Promise<void>;

  // Conversation management
  joinConversation: (conversationId: string) => void;
  leaveConversation: (conversationId: string) => void;
  setActiveConversation: (conversationId: string | null) => void;
  loadConversations: () => Promise<void>;

  // Message management
  sendMessage: (conversationId: string, content: string, type?: string) => void;
  loadMessages: (conversationId: string, page?: number) => Promise<void>;
  loadMoreMessages: (conversationId: string) => Promise<void>;

  // Typing indicators
  startTyping: (conversationId: string) => void;
  stopTyping: (conversationId: string) => void;

  // Presence management
  updatePresence: (status: 'ONLINE' | 'AWAY' | 'OFFLINE') => void;

  // Utilities
  clearError: () => void;
  getUnreadCount: (conversationId: string) => number;
  markAsRead: (conversationId: string) => void;
}

export const useChat = (): UseChatReturn => {
  const { user, token } = useAuth();
  const [state, setState] = useState<ChatState>({
    conversations: [],
    activeConversation: null,
    messages: {},
    onlineUsers: new Set(),
    typingUsers: {},
    isConnected: false,
    isLoading: false,
    error: null,
  });

  const messagePages = useRef<{ [conversationId: string]: number }>({});
  const hasMoreMessages = useRef<{ [conversationId: string]: boolean }>({});
  const typingTimeouts = useRef<{ [key: string]: NodeJS.Timeout }>({});

  // WebSocket event handlers
  const handleMessage = useCallback((message: ChatMessage) => {
    setState(prev => ({
      ...prev,
      messages: {
        ...prev.messages,
        [message.conversationId]: [
          ...(prev.messages[message.conversationId] || []),
          message
        ].sort((a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime())
      }
    }));
  }, []);

  const handleTyping = useCallback((event: TypingEvent) => {
    setState(prev => {
      const conversationTyping = new Set(prev.typingUsers[event.conversationId] || []);

      if (event.isTyping) {
        conversationTyping.add(event.userId);
      } else {
        conversationTyping.delete(event.userId);
      }

      return {
        ...prev,
        typingUsers: {
          ...prev.typingUsers,
          [event.conversationId]: conversationTyping
        }
      };
    });

    // Auto-clear typing indicator after timeout
    if (event.isTyping) {
      const timeoutKey = `${event.conversationId}-${event.userId}`;
      if (typingTimeouts.current[timeoutKey]) {
        clearTimeout(typingTimeouts.current[timeoutKey]);
      }

      typingTimeouts.current[timeoutKey] = setTimeout(() => {
        setState(prev => {
          const conversationTyping = new Set(prev.typingUsers[event.conversationId] || []);
          conversationTyping.delete(event.userId);

          return {
            ...prev,
            typingUsers: {
              ...prev.typingUsers,
              [event.conversationId]: conversationTyping
            }
          };
        });
      }, 3000);
    }
  }, []);

  const handlePresence = useCallback((event: PresenceEvent) => {
    setState(prev => {
      const newOnlineUsers = new Set(prev.onlineUsers);

      if (event.status === 'ONLINE') {
        newOnlineUsers.add(event.userId);
      } else {
        newOnlineUsers.delete(event.userId);
      }

      return {
        ...prev,
        onlineUsers: newOnlineUsers
      };
    });
  }, []);

  const handleConnected = useCallback(() => {
    setState(prev => ({ ...prev, isConnected: true, error: null }));
  }, []);

  const handleDisconnected = useCallback(() => {
    setState(prev => ({ ...prev, isConnected: false }));
  }, []);

  const handleError = useCallback((error: Error) => {
    setState(prev => ({ ...prev, error: error.message, isConnected: false }));
  }, []);

  // Connection management
  const connect = useCallback(async () => {
    if (!token) {
      setState(prev => ({ ...prev, error: 'Authentication token not available' }));
      return;
    }

    try {
      setState(prev => ({ ...prev, isLoading: true, error: null }));

      // Setup event listeners
      webSocketService.on('message', handleMessage);
      webSocketService.on('typing', handleTyping);
      webSocketService.on('presence', handlePresence);
      webSocketService.on('connected', handleConnected);
      webSocketService.on('disconnected', handleDisconnected);
      webSocketService.on('error', handleError);

      await webSocketService.connect(token);

      setState(prev => ({ ...prev, isLoading: false }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Connection failed',
        isLoading: false
      }));
    }
  }, [token, handleMessage, handleTyping, handlePresence, handleConnected, handleDisconnected, handleError]);

  const disconnect = useCallback(async () => {
    await webSocketService.disconnect();

    // Clear event listeners
    webSocketService.removeAllListeners();

    setState(prev => ({
      ...prev,
      isConnected: false,
      activeConversation: null,
      typingUsers: {}
    }));
  }, []);

  // Conversation management
  const joinConversation = useCallback((conversationId: string) => {
    if (state.isConnected) {
      webSocketService.joinConversation(conversationId);
    }
  }, [state.isConnected]);

  const leaveConversation = useCallback((conversationId: string) => {
    if (state.isConnected) {
      webSocketService.leaveConversation(conversationId);
    }
  }, [state.isConnected]);

  const setActiveConversation = useCallback((conversationId: string | null) => {
    setState(prev => {
      // Leave previous conversation
      if (prev.activeConversation && prev.activeConversation !== conversationId) {
        webSocketService.leaveConversation(prev.activeConversation);
      }

      // Join new conversation
      if (conversationId) {
        webSocketService.joinConversation(conversationId);
      }

      return { ...prev, activeConversation: conversationId };
    });
  }, []);

  const loadConversations = useCallback(async () => {
    try {
      setState(prev => ({ ...prev, isLoading: true }));

      const response: PaginatedResponse<any> = await chatService.getChatRooms();

      setState(prev => ({
        ...prev,
        conversations: response.content,
        isLoading: false
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Failed to load conversations',
        isLoading: false
      }));
    }
  }, []);

  // Message management
  const sendMessage = useCallback((conversationId: string, content: string, type = 'TEXT') => {
    if (state.isConnected) {
      webSocketService.sendMessage(conversationId, content, type);
    }
  }, [state.isConnected]);

  const loadMessages = useCallback(async (conversationId: string, page = 0) => {
    try {
      const response: PaginatedResponse<ChatMessage> = await chatService.getMessages(conversationId, page, 50);

      setState(prev => ({
        ...prev,
        messages: {
          ...prev.messages,
          [conversationId]: page === 0 ? response.content : [
            ...response.content,
            ...(prev.messages[conversationId] || [])
          ].sort((a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime())
        }
      }));

      messagePages.current[conversationId] = page;
      hasMoreMessages.current[conversationId] = !response.last;
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Failed to load messages'
      }));
    }
  }, []);

  const loadMoreMessages = useCallback(async (conversationId: string) => {
    const currentPage = messagePages.current[conversationId] || 0;
    const hasMore = hasMoreMessages.current[conversationId] !== false;

    if (hasMore) {
      await loadMessages(conversationId, currentPage + 1);
    }
  }, [loadMessages]);

  // Typing indicators
  const startTyping = useCallback((conversationId: string) => {
    if (state.isConnected) {
      webSocketService.sendTyping(conversationId, true);
    }
  }, [state.isConnected]);

  const stopTyping = useCallback((conversationId: string) => {
    if (state.isConnected) {
      webSocketService.sendTyping(conversationId, false);
    }
  }, [state.isConnected]);

  // Presence management
  const updatePresence = useCallback((status: 'ONLINE' | 'AWAY' | 'OFFLINE') => {
    if (state.isConnected) {
      webSocketService.updatePresence(status);
    }
  }, [state.isConnected]);

  // Utilities
  const clearError = useCallback(() => {
    setState(prev => ({ ...prev, error: null }));
  }, []);

  const getUnreadCount = useCallback((conversationId: string) => {
    const messages = state.messages[conversationId] || [];
    return messages.filter(msg => msg.senderId !== user?.id && !msg.readBy?.includes(user?.id || '')).length;
  }, [state.messages, user?.id]);

  const markAsRead = useCallback(async (conversationId: string) => {
    try {
      await chatService.markRoomAsRead(conversationId);

      setState(prev => ({
        ...prev,
        messages: {
          ...prev.messages,
          [conversationId]: prev.messages[conversationId]?.map(msg => ({
            ...msg,
            readBy: [...(msg.readBy || []), user?.id || '']
          })) || []
        }
      }));
    } catch (error) {
      console.error('Failed to mark messages as read:', error);
    }
  }, [user?.id]);

  // Auto-connect when token is available
  useEffect(() => {
    if (token && !state.isConnected && !state.isLoading) {
      connect();
    }
  }, [token, state.isConnected, state.isLoading, connect]);

  // Auto-disconnect on unmount
  useEffect(() => {
    return () => {
      disconnect();

      // Clear all timeouts
      Object.values(typingTimeouts.current).forEach(timeout => clearTimeout(timeout));
    };
  }, [disconnect]);

  return {
    ...state,
    connect,
    disconnect,
    joinConversation,
    leaveConversation,
    setActiveConversation,
    loadConversations,
    sendMessage,
    loadMessages,
    loadMoreMessages,
    startTyping,
    stopTyping,
    updatePresence,
    clearError,
    getUnreadCount,
    markAsRead,
  };
};

import { useState, useEffect, useCallback, useRef } from 'react';
import { webSocketService, TypingEvent, PresenceEvent } from '@/services/websocket.service';
import { chatService } from '@/features/chat/services/chat.service';
import { useAuth } from '@/contexts/AuthContext';
import { PaginatedResponse } from '@/shared/types';
import { ChatMessage, ChatRoom } from '@/shared/types/chat';

export interface ChatState {
  conversations: any[];
  activeConversation: string | null;
  messages: { [conversationId: string]: ChatMessage[] };
  onlineUsers: Set<string>;
  typingUsers: { [conversationId: string]: Set<string> };
  isConnected: boolean;
  isLoading: boolean;
  error: string | null;
}

export interface UseChatReturn extends ChatState {
  // Connection management
  connect: () => Promise<void>;
  disconnect: () => Promise<void>;

  // Conversation management
  joinConversation: (conversationId: string) => void;
  leaveConversation: (conversationId: string) => void;
  setActiveConversation: (conversationId: string | null) => void;
  loadConversations: () => Promise<void>;

  // Message management
  sendMessage: (conversationId: string, content: string, type?: string) => void;
  loadMessages: (conversationId: string, page?: number) => Promise<void>;
  loadMoreMessages: (conversationId: string) => Promise<void>;

  // Typing indicators
  startTyping: (conversationId: string) => void;
  stopTyping: (conversationId: string) => void;

  // Presence management
  updatePresence: (status: 'ONLINE' | 'AWAY' | 'OFFLINE') => void;

  // Utilities
  clearError: () => void;
  getUnreadCount: (conversationId: string) => number;
  markAsRead: (conversationId: string) => void;
}

export const useChat = (): UseChatReturn => {
  const { user, token } = useAuth();
  const [state, setState] = useState<ChatState>({
    conversations: [],
    activeConversation: null,
    messages: {},
    onlineUsers: new Set(),
    typingUsers: {},
    isConnected: false,
    isLoading: false,
    error: null,
  });

  const messagePages = useRef<{ [conversationId: string]: number }>({});
  const hasMoreMessages = useRef<{ [conversationId: string]: boolean }>({});
  const typingTimeouts = useRef<{ [key: string]: NodeJS.Timeout }>({});

  // WebSocket event handlers
  const handleMessage = useCallback((message: ChatMessage) => {
    setState(prev => ({
      ...prev,
      messages: {
        ...prev.messages,
        [message.conversationId]: [
          ...(prev.messages[message.conversationId] || []),
          message
        ].sort((a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime())
      }
    }));
  }, []);

  const handleTyping = useCallback((event: TypingEvent) => {
    setState(prev => {
      const conversationTyping = new Set(prev.typingUsers[event.conversationId] || []);

      if (event.isTyping) {
        conversationTyping.add(event.userId);
      } else {
        conversationTyping.delete(event.userId);
      }

      return {
        ...prev,
        typingUsers: {
          ...prev.typingUsers,
          [event.conversationId]: conversationTyping
        }
      };
    });

    // Auto-clear typing indicator after timeout
    if (event.isTyping) {
      const timeoutKey = `${event.conversationId}-${event.userId}`;
      if (typingTimeouts.current[timeoutKey]) {
        clearTimeout(typingTimeouts.current[timeoutKey]);
      }

      typingTimeouts.current[timeoutKey] = setTimeout(() => {
        setState(prev => {
          const conversationTyping = new Set(prev.typingUsers[event.conversationId] || []);
          conversationTyping.delete(event.userId);

          return {
            ...prev,
            typingUsers: {
              ...prev.typingUsers,
              [event.conversationId]: conversationTyping
            }
          };
        });
      }, 3000);
    }
  }, []);

  const handlePresence = useCallback((event: PresenceEvent) => {
    setState(prev => {
      const newOnlineUsers = new Set(prev.onlineUsers);

      if (event.status === 'ONLINE') {
        newOnlineUsers.add(event.userId);
      } else {
        newOnlineUsers.delete(event.userId);
      }

      return {
        ...prev,
        onlineUsers: newOnlineUsers
      };
    });
  }, []);

  const handleConnected = useCallback(() => {
    setState(prev => ({ ...prev, isConnected: true, error: null }));
  }, []);

  const handleDisconnected = useCallback(() => {
    setState(prev => ({ ...prev, isConnected: false }));
  }, []);

  const handleError = useCallback((error: Error) => {
    setState(prev => ({ ...prev, error: error.message, isConnected: false }));
  }, []);

  // Connection management
  const connect = useCallback(async () => {
    if (!token) {
      setState(prev => ({ ...prev, error: 'Authentication token not available' }));
      return;
    }

    try {
      setState(prev => ({ ...prev, isLoading: true, error: null }));

      // Setup event listeners
      webSocketService.on('message', handleMessage);
      webSocketService.on('typing', handleTyping);
      webSocketService.on('presence', handlePresence);
      webSocketService.on('connected', handleConnected);
      webSocketService.on('disconnected', handleDisconnected);
      webSocketService.on('error', handleError);

      await webSocketService.connect(token);

      setState(prev => ({ ...prev, isLoading: false }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Connection failed',
        isLoading: false
      }));
    }
  }, [token, handleMessage, handleTyping, handlePresence, handleConnected, handleDisconnected, handleError]);

  const disconnect = useCallback(async () => {
    await webSocketService.disconnect();

    // Clear event listeners
    webSocketService.removeAllListeners();

    setState(prev => ({
      ...prev,
      isConnected: false,
      activeConversation: null,
      typingUsers: {}
    }));
  }, []);

  // Conversation management
  const joinConversation = useCallback((conversationId: string) => {
    if (state.isConnected) {
      webSocketService.joinConversation(conversationId);
    }
  }, [state.isConnected]);

  const leaveConversation = useCallback((conversationId: string) => {
    if (state.isConnected) {
      webSocketService.leaveConversation(conversationId);
    }
  }, [state.isConnected]);

  const setActiveConversation = useCallback((conversationId: string | null) => {
    setState(prev => {
      // Leave previous conversation
      if (prev.activeConversation && prev.activeConversation !== conversationId) {
        webSocketService.leaveConversation(prev.activeConversation);
      }

      // Join new conversation
      if (conversationId) {
        webSocketService.joinConversation(conversationId);
      }

      return { ...prev, activeConversation: conversationId };
    });
  }, []);

  const loadConversations = useCallback(async () => {
    try {
      setState(prev => ({ ...prev, isLoading: true }));

      const response: PaginatedResponse<any> = await chatService.getChatRooms();

      setState(prev => ({
        ...prev,
        conversations: response.content,
        isLoading: false
      }));
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Failed to load conversations',
        isLoading: false
      }));
    }
  }, []);

  // Message management
  const sendMessage = useCallback((conversationId: string, content: string, type = 'TEXT') => {
    if (state.isConnected) {
      webSocketService.sendMessage(conversationId, content, type);
    }
  }, [state.isConnected]);

  const loadMessages = useCallback(async (conversationId: string, page = 0) => {
    try {
      const response: PaginatedResponse<ChatMessage> = await chatService.getMessages(conversationId, page, 50);

      setState(prev => ({
        ...prev,
        messages: {
          ...prev.messages,
          [conversationId]: page === 0 ? response.content : [
            ...response.content,
            ...(prev.messages[conversationId] || [])
          ].sort((a, b) => new Date(a.timestamp).getTime() - new Date(b.timestamp).getTime())
        }
      }));

      messagePages.current[conversationId] = page;
      hasMoreMessages.current[conversationId] = !response.last;
    } catch (error) {
      setState(prev => ({
        ...prev,
        error: error instanceof Error ? error.message : 'Failed to load messages'
      }));
    }
  }, []);

  const loadMoreMessages = useCallback(async (conversationId: string) => {
    const currentPage = messagePages.current[conversationId] || 0;
    const hasMore = hasMoreMessages.current[conversationId] !== false;

    if (hasMore) {
      await loadMessages(conversationId, currentPage + 1);
    }
  }, [loadMessages]);

  // Typing indicators
  const startTyping = useCallback((conversationId: string) => {
    if (state.isConnected) {
      webSocketService.sendTyping(conversationId, true);
    }
  }, [state.isConnected]);

  const stopTyping = useCallback((conversationId: string) => {
    if (state.isConnected) {
      webSocketService.sendTyping(conversationId, false);
    }
  }, [state.isConnected]);

  // Presence management
  const updatePresence = useCallback((status: 'ONLINE' | 'AWAY' | 'OFFLINE') => {
    if (state.isConnected) {
      webSocketService.updatePresence(status);
    }
  }, [state.isConnected]);

  // Utilities
  const clearError = useCallback(() => {
    setState(prev => ({ ...prev, error: null }));
  }, []);

  const getUnreadCount = useCallback((conversationId: string) => {
    const messages = state.messages[conversationId] || [];
    return messages.filter(msg => 
      msg.senderId !== user?.id && 
      !msg.readBy?.some(readStatus => readStatus.userId === user?.id)
    ).length;
  }, [state.messages, user?.id]);

  const markAsRead = useCallback(async (conversationId: string) => {
    try {
      await chatService.markRoomAsRead(conversationId);

      setState(prev => ({
        ...prev,
        messages: {
          ...prev.messages,
          [conversationId]: prev.messages[conversationId]?.map(msg => {
            // Check if user already read this message
            const alreadyRead = msg.readBy?.some(readStatus => readStatus.userId === user?.id);

            if (alreadyRead) {
              return msg;
            }

            // Add read status for current user
            const newReadStatus = {
              userId: user?.id || '',
              user: user!,
              readAt: new Date().toISOString()
            };

            return {
              ...msg,
              readBy: [...(msg.readBy || []), newReadStatus],
              isRead: true
            };
          }) || []
        }
      }));
    } catch (error) {
      console.error('Failed to mark messages as read:', error);
    }
  }, [user]);

  // Auto-connect when token is available
  useEffect(() => {
    if (token && !state.isConnected && !state.isLoading) {
      connect();
    }
  }, [token, state.isConnected, state.isLoading, connect]);

  // Auto-disconnect on unmount
  useEffect(() => {
    return () => {
      disconnect();

      // Clear all timeouts
      Object.values(typingTimeouts.current).forEach(timeout => clearTimeout(timeout));
    };
  }, [disconnect]);

  return {
    ...state,
    connect,
    disconnect,
    joinConversation,
    leaveConversation,
    setActiveConversation,
    loadConversations,
    sendMessage,
    loadMessages,
    loadMoreMessages,
    startTyping,
    stopTyping,
    updatePresence,
    clearError,
    getUnreadCount,
    markAsRead,
  };
};

