package com.ctuconnect.client;

import com.ctuconnect.dto.AuthorInfo;
import lombok.extern.slf4j.Slf4j;
import org.springframework.stereotype.Component;

import java.util.HashSet;
import java.util.Set;

/**
 * Fallback implementation for UserServiceClient when user-service is unavailable
 * Provides default values to prevent service failures
 */
@Component
@Slf4j
public class UserServiceClientFallback implements UserServiceClient {

    @Override
    public AuthorInfo getAuthorInfo(String authorId) {
        log.warn("User service unavailable, returning fallback author info for user: {}", authorId);
        return AuthorInfo.builder()
                .userId(authorId)
                .username("Unknown User")
                .fullName("Unknown User")
                .avatarUrl(null)
                .isVerified(false)
                .build();
    }

    @Override
    public Set<String> getFriendIds(String userId) {
        log.warn("User service unavailable, returning empty friend list for user: {}", userId);
        return new HashSet<>();
    }

    @Override
    public Set<String> getCloseInteractionIds(String userId) {
        log.warn("User service unavailable, returning empty close interactions for user: {}", userId);
        return new HashSet<>();
    }

    @Override
    public Set<String> getSameFacultyUserIds(String userId) {
        log.warn("User service unavailable, returning empty same faculty users for user: {}", userId);
        return new HashSet<>();
    }

    @Override
    public Set<String> getSameMajorUserIds(String userId) {
        log.warn("User service unavailable, returning empty same major users for user: {}", userId);
        return new HashSet<>();
    }

    @Override
    public Set<String> getUserInterestTags(String userId) {
        log.warn("User service unavailable, returning empty interest tags for user: {}", userId);
        return new HashSet<>();
    }

    @Override
    public Set<String> getUserPreferredCategories(String userId) {
        log.warn("User service unavailable, returning empty preferred categories for user: {}", userId);
        return new HashSet<>();
    }

    @Override
    public String getUserFacultyId(String userId) {
        log.warn("User service unavailable, returning null faculty ID for user: {}", userId);
        return null;
    }

    @Override
    public String getUserMajorId(String userId) {
        log.warn("User service unavailable, returning null major ID for user: {}", userId);
        return null;
    }
}

package com.ctuconnect.config;

import org.springframework.beans.factory.annotation.Value;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.data.redis.connection.RedisConnectionFactory;
import org.springframework.data.redis.connection.lettuce.LettuceConnectionFactory;
import org.springframework.data.redis.core.RedisTemplate;
import org.springframework.data.redis.serializer.GenericJackson2JsonRedisSerializer;
import org.springframework.data.redis.serializer.StringRedisSerializer;

@Configuration
public class CacheConfig {

    @Value("${spring.data.redis.host:localhost}")
    private String redisHost;

    @Value("${spring.data.redis.port:6379}")
    private int redisPort;

    @Bean
    public RedisConnectionFactory redisConnectionFactory() {
        return new LettuceConnectionFactory(redisHost, redisPort);
    }

    @Bean
    public RedisTemplate<String, Object> redisTemplate(RedisConnectionFactory connectionFactory) {
        RedisTemplate<String, Object> template = new RedisTemplate<>();
        template.setConnectionFactory(connectionFactory);

        // Use String serializer for keys
        template.setKeySerializer(new StringRedisSerializer());
        template.setHashKeySerializer(new StringRedisSerializer());

        // Use JSON serializer for values
        template.setValueSerializer(new GenericJackson2JsonRedisSerializer());
        template.setHashValueSerializer(new GenericJackson2JsonRedisSerializer());

        template.afterPropertiesSet();
        return template;
    }
}

package com.ctuconnect.config;

import feign.Logger;
import feign.Request;
import feign.Retryer;
import feign.codec.ErrorDecoder;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;

import java.util.concurrent.TimeUnit;

/**
 * Feign client configuration for better error handling and retry mechanism
 */
@Configuration
public class FeignConfig {

    @Bean
    Logger.Level feignLoggerLevel() {
        return Logger.Level.BASIC;
    }

    @Bean
    public Request.Options requestOptions() {
        return new Request.Options(
                10, TimeUnit.SECONDS, // connect timeout
                60, TimeUnit.SECONDS, // read timeout
                true // follow redirects
        );
    }

    @Bean
    public Retryer retryer() {
        return new Retryer.Default(
                1000, // initial interval
                3000, // max interval
                3     // max attempts
        );
    }

    @Bean
    public ErrorDecoder errorDecoder() {
        return new CustomFeignErrorDecoder();
    }

    /**
     * Custom error decoder to handle service communication errors gracefully
     */
    public static class CustomFeignErrorDecoder implements ErrorDecoder {

        @Override
        public Exception decode(String methodKey, feign.Response response) {
            switch (response.status()) {
                case 400:
                    return new IllegalArgumentException("Bad Request: " + methodKey);
                case 404:
                    return new RuntimeException("Service not found: " + methodKey);
                case 500:
                    return new RuntimeException("Internal Server Error in " + methodKey);
                case 503:
                    return new RuntimeException("Service Unavailable: " + methodKey);
                default:
                    return new RuntimeException("Unknown error occurred in " + methodKey + ": " + response.status());
            }
        }
    }
}

package com.ctuconnect.config;

import org.springframework.boot.actuator.health.Health;
import org.springframework.boot.actuator.health.HealthIndicator;
import org.springframework.context.annotation.Bean;
import org.springframework.context.annotation.Configuration;
import org.springframework.data.redis.connection.RedisConnectionFactory;
import org.springframework.data.mongodb.core.MongoTemplate;

@Configuration
public class HealthCheckConfig {

    @Bean
    public HealthIndicator redisHealthIndicator(RedisConnectionFactory redisConnectionFactory) {
        return () -> {
            try {
                redisConnectionFactory.getConnection().ping();
                return Health.up()
                    .withDetail("redis", "Available")
                    .build();
            } catch (Exception e) {
                return Health.down()
                    .withDetail("redis", "Unavailable")
                    .withDetail("error", e.getMessage())
                    .build();
            }
        };
    }

    @Bean
    public HealthIndicator mongoHealthIndicator(MongoTemplate mongoTemplate) {
        return () -> {
            try {
                mongoTemplate.getCollection("health_check").countDocuments();
                return Health.up()
                    .withDetail("mongodb", "Available")
                    .build();
            } catch (Exception e) {
                return Health.down()
                    .withDetail("mongodb", "Unavailable")
                    .withDetail("error", e.getMessage())
                    .build();
            }
        };
    }
}

package com.ctuconnect.config;

import org.springframework.context.annotation.Configuration;
import org.springframework.messaging.simp.config.MessageBrokerRegistry;
import org.springframework.web.socket.config.annotation.EnableWebSocketMessageBroker;
import org.springframework.web.socket.config.annotation.StompEndpointRegistry;
import org.springframework.web.socket.config.annotation.WebSocketMessageBrokerConfigurer;

@Configuration
@EnableWebSocketMessageBroker
public class WebSocketConfig implements WebSocketMessageBrokerConfigurer {

    @Override
    public void configureMessageBroker(MessageBrokerRegistry config) {
        // Enable simple message broker for in-memory message handling
        config.enableSimpleBroker("/topic", "/queue");
        config.setApplicationDestinationPrefixes("/app");
        config.setUserDestinationPrefix("/user");
    }

    @Override
    public void registerStompEndpoints(StompEndpointRegistry registry) {
        // Register STOMP endpoints for WebSocket connections
        registry.addEndpoint("/ws")
                .setAllowedOriginPatterns("*")
                .withSockJS();

        registry.addEndpoint("/ws-notifications")
                .setAllowedOriginPatterns("*")
                .withSockJS();
    }
}

